   559→// ============================================================================
   560→// EXPORT
   561→// ============================================================================
   562→
   563→/**
   564→ * Export for selected format
   565→ */
   566→async function exportReframe() {
   567→  if (!socialReframeState.analysis) {
   568→    showReframeStatus('Analyze video first', 'error');
   569→    return;
   570→  }
   571→
   572→  socialReframeState.isExporting = true;
   573→  updateExportButton(true);
   574→  showReframeStatus('Generating export data...', 'info');
   575→
   576→  try {
   577→    const backendUrl = typeof getBackendUrl === 'function' ? getBackendUrl() : 'https://127.0.0.1:3847';
   578→    const headers = typeof getAuthHeaders === 'function' ? getAuthHeaders() : { 'Content-Type': 'application/json' };
   579→    const fetchFn = typeof fetchWithTimeout === 'function' ? fetchWithTimeout : fetch;
   580→
   581→    const slider = reframeElements.smoothingSlider || document.getElementById('motion-smoothing-slider');
   582→    const smoothing = slider?.value || 0.3;
   583→
   584→    // FIX: CEP-CRIT-015 - Social Reframe Timeouts (120s for single export)
   585→    const response = await fetchFn(`${backendUrl}/reframe/export`, {
   586→      method: 'POST',
   587→      headers,
   588→      body: JSON.stringify({
   589→        videoPath: socialReframeState.currentVideoPath,
   590→        formats: [socialReframeState.selectedAspectRatio],
   591→        settings: {
   592→          quality: 'high',
   593→          motionSmoothing: parseFloat(smoothing)
   594→        }
   595→      })
   596→    }, 120000);
   597→
   598→    if (!response.ok) {
   599→      const err = await response.json();
   600→      throw new Error(err.error || 'Export failed');
   601→    }
   602→
   603→    const data = await response.json();
   604→    renderExportResults(data);
   605→    showReframeStatus('Export data generated', 'success');
   606→
   607→  } catch (err) {
   608→    console.error('[SPLICE] Reframe export error:', err);
   609→    showReframeStatus(err.message, 'error');
   610→  } finally {
   611→    socialReframeState.isExporting = false;
   612→    updateExportButton(false);
   613→  }
   614→}
   615→
   616→/**
   617→ * Export all formats at once
   618→ */
   619→async function exportAllFormats() {
   620→  if (!socialReframeState.analysis) {
   621→    showReframeStatus('Analyze video first', 'error');
   622→    return;
   623→  }
   624→
   625→  const allFormats = socialReframeState.aspectRatios.map(a => a.id);
   626→
   627→  socialReframeState.isExporting = true;
   628→  updateExportButton(true);
   629→  showReframeStatus('Generating all formats...', 'info');
   630→
   631→  try {
   632→    const backendUrl = typeof getBackendUrl === 'function' ? getBackendUrl() : 'https://127.0.0.1:3847';
   633→    const headers = typeof getAuthHeaders === 'function' ? getAuthHeaders() : { 'Content-Type': 'application/json' };
   634→    const fetchFn = typeof fetchWithTimeout === 'function' ? fetchWithTimeout : fetch;
   635→
   636→    // FIX: CEP-CRIT-015 - Social Reframe Timeouts (180s for batch export)
   637→    const response = await fetchFn(`${backendUrl}/reframe/export`, {
   638→      method: 'POST',
   639→      headers,
   640→      body: JSON.stringify({
   641→        videoPath: socialReframeState.currentVideoPath,
   642→        formats: allFormats,
   643→        settings: { quality: 'high' }
   644→      })
   645→    }, 180000);
   646→
   647→    if (!response.ok) {
   648→      const err = await response.json();
   649→      throw new Error(err.error || 'Export failed');
   650→    }
   651→
   652→    const data = await response.json();
   653→    renderExportResults(data);
   654→    showReframeStatus(`Generated ${data.totalFormats} formats`, 'success');
   655→
   656→  } catch (err) {
   657→    console.error('[SPLICE] Reframe batch export error:', err);
   658→    showReframeStatus(err.message, 'error');
   659→  } finally {
   660→    socialReframeState.isExporting = false;
   661→    updateExportButton(false);
   662→  }
   663→}
   664→
   665→/**
   666→ * Render export results
   667→ */
   668→function renderExportResults(data) {
   669→  const container = reframeElements.exportResults || document.getElementById('reframe-export-results');
   670→  if (!container) return;
   671→
   672→  // SECURITY: Escape export data to prevent XSS
   673→  const html = `
   674→    <div class="export-summary">
   675→      <span class="export-count">${escapeHtmlReframe(String(data.totalFormats))} format(s) ready</span>
   676→    </div>
   677→    <div class="export-list">
   678→      ${data.exports.map(exp => `
   679→        <div class="export-item">
   680→          <span class="export-format">${escapeHtmlReframe(exp.name)}</span>
   681→          <span class="export-dims">${escapeHtmlReframe(String(exp.dimensions.width))}x${escapeHtmlReframe(String(exp.dimensions.height))}</span>
   682→          <button class="copy-ffmpeg-btn btn btn-small" data-filter="${encodeURIComponent(exp.ffmpegFilter)}">
   683→            Copy FFmpeg
   684→          </button>
   685→        </div>
   686→      `).join('')}
   687→    </div>
   688→  `;
   689→
   690→  container.innerHTML = html;
   691→
   692→  // Add copy handlers
   693→  container.querySelectorAll('.copy-ffmpeg-btn').forEach(btn => {
   694→    btn.addEventListener('click', () => {
   695→      const filter = decodeURIComponent(btn.dataset.filter);
   696→      navigator.clipboard.writeText(filter).then(() => {
   697→        btn.textContent = 'Copied!';
   698→        setTimeout(() => btn.textContent = 'Copy FFmpeg', 2000);
   699→      });
   700→    });
   701→  });
   702→}
   703→
   704→// ============================================================================
   705→// UI HELPERS
   706→// ============================================================================
   707→
   708→/**
   709→ * Show status message
   710→ */
   711→function showReframeStatus(message, type = 'info') {
   712→  const statusEl = reframeElements.status || document.getElementById('reframe-status');
   713→  if (!statusEl) return;
   714→
   715→  statusEl.textContent = message;
   716→  statusEl.className = `reframe-status reframe-status-${type}`;
   717→
   718→  if (type !== 'error') {
   719→    setTimeout(() => {
   720→      statusEl.textContent = '';
   721→      statusEl.className = 'reframe-status';
   722→    }, 5000);
   723→  }
   724→}
   725→
   726→/**
   727→ * Update analyze button state
   728→ */
   729→function updateAnalyzeButton(isAnalyzing) {
   730→  const btn = reframeElements.analyzeBtn || document.getElementById('analyze-reframe-btn');
   731→  if (!btn) return;
   732→
   733→  btn.disabled = isAnalyzing;
   734→  btn.textContent = isAnalyzing ? 'Analyzing...' : 'Analyze Video';
   735→}
   736→
   737→/**
   738→ * Update export button state
   739→ */
   740→function updateExportButton(isExporting) {
   741→  const btn = reframeElements.exportBtn || document.getElementById('export-reframe-btn');
   742→  if (!btn) return;
   743→
   744→  btn.disabled = isExporting;
   745→  btn.textContent = isExporting ? 'Exporting...' : 'Export';
   746→}
   747→
   748→/**
   749→ * Update smoothing value display
   750→ */
   751→function updateSmoothingValue(value) {
   752→  const display = reframeElements.smoothingDisplay || document.getElementById('smoothing-value-display');
   753→  if (display) {
   754→    display.textContent = parseFloat(value).toFixed(2);
   755→  }
   756→}
   757→
   758→/**
   759→ * Format duration for display
   760→ */
   761→function formatDuration(seconds) {
   762→  const mins = Math.floor(seconds / 60);
   763→  const secs = Math.floor(seconds % 60);
   764→  return `${mins}:${secs.toString().padStart(2, '0')}`;
   765→}
   766→
   767→// ============================================================================
   768→// EXPORTS
   769→// ============================================================================
   770→
   771→// Export for global access
   772→window.spliceSocialReframe = {
   773→  init: initSocialReframe,
   774→  analyze: analyzeForReframe,
   775→  setVideoPath: setVideoPath,
   776→  selectPlatform: selectPlatform,
   777→  selectAspectRatio: selectAspectRatio,
   778→  export: exportReframe,
   779→  exportAll: exportAllFormats,
   780→  toggleSafeZones: toggleSafeZones,
   781→  getState: () => ({ ...socialReframeState })
   782→};
   783→
   784→// Also expose individual functions for direct access
   785→window.initSocialReframe = initSocialReframe;
