   775→
   776→function isValidYouTubeUrl(url) {
   777→  if (!url) return false;
   778→  const patterns = [
   779→    /youtube\.com\/watch\?v=[\w-]{11}/,
   780→    /youtu\.be\/[\w-]{11}/,
   781→    /youtube\.com\/embed\/[\w-]{11}/
   782→  ];
   783→  return patterns.some(p => p.test(url));
   784→}
