  1279→                }
  1280→
  1281→                // Fall back to default preset
  1282→                if (!presetPath) {
  1283→                    presetPath = app.encoder.getDefaultPresetPath();
  1284→                }
  1285→            } catch (e) {
  1286→                presetPath = app.encoder.getDefaultPresetPath();
  1287→            }
  1288→
  1289→            // Start the encode
  1290→            var success = app.encoder.encodeSequence(
  1291→                seq,
  1292→                outputPath,
  1293→                presetPath,
  1294→                0, // WorkAreaType: 0 = entire sequence
  1295→                false // removeOnCompletion
  1296→            );
  1297→
  1298→            if (success) {
  1299→                return JSON.stringify({ success: true, outputPath: outputPath, method: "ame" });
  1300→            }
  1301→        }
  1302→
  1303→        // Fallback: Try to get the source audio path from first clip
  1304→        return getFirstClipAudioPath();
  1305→
  1306→    } catch (e) {
  1307→        return JSON.stringify({ error: e.message });
  1308→    }
  1309→}
  1310→
  1311→/**
  1312→ * Get the audio/video file path from the first clip in the timeline
  1313→ * Used as fallback when AME export is not available
  1314→ * @returns {Object} { success, path } or { error }
  1315→ */
  1316→function getFirstClipAudioPath() {
  1317→    try {
  1318→        var seq = app.project.activeSequence;
  1319→        if (!seq) return JSON.stringify({ error: "No active sequence" });
  1320→
  1321→        // First try audio tracks
  1322→        for (var i = 0; i < seq.audioTracks.numTracks; i++) {
  1323→            var track = seq.audioTracks[i];
  1324→            if (track.clips.numItems > 0) {
  1325→                var clip = track.clips[0];
  1326→                if (clip.projectItem && clip.projectItem.getMediaPath) {
  1327→                    var mediaPath = clip.projectItem.getMediaPath();
  1328→                    if (mediaPath && mediaPath.length > 0) {
  1329→                        return JSON.stringify({ success: true, path: mediaPath, method: "audio_track" });
  1330→                    }
  1331→                }
  1332→            }
  1333→        }
  1334→
  1335→        // Fall back to video tracks (which may have linked audio)
  1336→        for (var i = 0; i < seq.videoTracks.numTracks; i++) {
  1337→            var track = seq.videoTracks[i];
  1338→            if (track.clips.numItems > 0) {
  1339→                var clip = track.clips[0];
  1340→                if (clip.projectItem && clip.projectItem.getMediaPath) {
  1341→                    var mediaPath = clip.projectItem.getMediaPath();
  1342→                    if (mediaPath && mediaPath.length > 0) {
  1343→                        return JSON.stringify({ success: true, path: mediaPath, method: "video_track" });
  1344→                    }
  1345→                }
  1346→            }
  1347→        }
  1348→
  1349→        return JSON.stringify({ error: "No clips with media paths found in sequence" });
  1350→    } catch (e) {
  1351→        return JSON.stringify({ error: e.message });
  1352→    }
  1353→}
  1354→
  1355→// ============================================================================
  1356→// UTILITY FUNCTIONS
  1357→// ============================================================================
  1358→
  1359→function moveClipByNTracks(currentTrackId, clipStartTime, nTracksToMove, trackType) {
  1360→    if (!isQEAvailable() && !enableQE()) {
  1361→        return JSON.stringify({ error: "QE not available for moving clips between tracks" });
  1362→    }
  1363→
  1364→    try {
  1365→        var accuracy = 3;
  1366→        var search_start_time = clipStartTime.toFixed(accuracy);
  1367→
  1368→        if (trackType == "audio") {
  1369→            var numClipsInTrack = qe.project.getActiveSequence().getAudioTrackAt(currentTrackId).numItems;
  1370→            for (var j = 0; j < numClipsInTrack; j++) {
  1371→                if (qe.project.getActiveSequence().getAudioTrackAt(currentTrackId).getItemAt(j).start.secs.toFixed(accuracy) == search_start_time) {
  1372→                    qe.project.getActiveSequence().getAudioTrackAt(currentTrackId).getItemAt(j).moveToTrack(0, nTracksToMove, "0");
  1373→                    return JSON.stringify({ success: true });
  1374→                }
  1375→            }
  1376→        } else if (trackType == "video") {
  1377→            var numClipsInTrack = qe.project.getActiveSequence().getVideoTrackAt(currentTrackId).numItems;
  1378→            for (var j = 0; j < numClipsInTrack; j++) {
  1379→                if (qe.project.getActiveSequence().getVideoTrackAt(currentTrackId).getItemAt(j).start.secs.toFixed(accuracy) == search_start_time) {
  1380→                    qe.project.getActiveSequence().getVideoTrackAt(currentTrackId).getItemAt(j).moveToTrack(nTracksToMove, 0, "0");
  1381→                    return JSON.stringify({ success: true });
  1382→                }
  1383→            }
  1384→        }
  1385→        return JSON.stringify({ error: "Clip not found at specified time" });
  1386→    } catch (e) {
  1387→        return JSON.stringify({ error: "Move clip failed: " + e.message });
  1388→    }
  1389→}
  1390→
  1391→function linkIdenticalClipsInTracks(videoTrackIndex, audioTrackIndex) {
  1392→    var selected_clips = app.project.activeSequence.getSelection();
  1393→    for (var i = 0; i < selected_clips.length; i++) {
  1394→        selected_clips[i].setSelected(false, false);
  1395→    }
  1396→
  1397→    var video_clips = app.project.activeSequence.videoTracks[videoTrackIndex].clips;
  1398→    var audio_clips = app.project.activeSequence.audioTracks[audioTrackIndex].clips;
  1399→
  1400→    for (var i = 0; i < video_clips.numItems; i++) {
  1401→        var video_clip = video_clips[i];
  1402→        var video_start = video_clip.start.seconds;
  1403→
  1404→        for (var j = 0; j < audio_clips.numItems; j++) {
  1405→            var audio_clip = audio_clips[j];
  1406→            var audio_start = audio_clip.start.seconds;
  1407→
  1408→            if (Math.abs(video_start - audio_start) < time_tolerance) {
  1409→                video_clip.setSelected(true, false);
  1410→                audio_clip.setSelected(true, false);
  1411→                app.project.activeSequence.linkSelection();
  1412→                video_clip.setSelected(false, false);
  1413→                audio_clip.setSelected(false, false);
  1414→                break;
  1415→            }
  1416→        }
  1417→    }
  1418→    return true;
  1419→}
  1420→
  1421→// ============================================================================
  1422→// ZOOM OPERATIONS (Phase 3)
  1423→// ============================================================================
  1424→
  1425→function addZoomMarker(startSeconds, scale, duration, easing) {
  1426→    try {
  1427→        var name = "ZOOM: " + scale + "%";
  1428→        var comments = "Duration: " + duration + "s | Easing: " + (easing || "ease-in-out");
  1429→        return createMarker(startSeconds, name, duration, comments, COLOR_LABELS.YELLOW);
  1430→    } catch (e) {
  1431→        return JSON.stringify({ error: e.message });
  1432→    }
  1433→}
  1434→
  1435→// ============================================================================
  1436→// CHAPTER OPERATIONS (Phase 3)
  1437→// ============================================================================
  1438→
  1439→function addChapterMarker(timeSeconds, title, description) {
  1440→    try {
  1441→        var seq = app.project.activeSequence;
  1442→        if (!seq) return JSON.stringify({ error: "No active sequence" });
  1443→
  1444→        var markers = seq.markers;
  1445→        var marker = markers.createMarker(timeSeconds);
  1446→
  1447→        marker.name = title;
  1448→        if (description) marker.comments = description;
  1449→        marker.setColorByIndex(COLOR_LABELS.BLUE);
  1450→        marker.setTypeAsChapter();
  1451→
  1452→        return JSON.stringify({
  1453→            success: true,
  1454→            guid: marker.guid
  1455→        });
  1456→    } catch (e) {
  1457→        return JSON.stringify({ error: e.message });
  1458→    }
  1459→}
  1460→
  1461→function getChapterMarkers() {
  1462→    try {
  1463→        var seq = app.project.activeSequence;
  1464→        if (!seq) return JSON.stringify({ error: "No active sequence" });
  1465→
  1466→        var markers = seq.markers;
  1467→        var chapters = [];
  1468→
  1469→        for (var i = 0; i < markers.numMarkers; i++) {
  1470→            var marker = markers[i];
  1471→            if (marker.type === "Chapter") {
  1472→                chapters.push({
  1473→                    guid: marker.guid,
  1474→                    name: marker.name,
  1475→                    comments: marker.comments,
  1476→                    start: marker.start.seconds
  1477→                });
  1478→            }
  1479→        }
  1480→
  1481→        return JSON.stringify(chapters);
  1482→    } catch (e) {
  1483→        return JSON.stringify({ error: e.message });
  1484→    }
  1485→}
  1486→
  1487→// ============================================================================
  1488→// UNDO OPERATIONS
  1489→// ============================================================================
  1490→
  1491→function undo() {
  1492→    app.project.undo();
  1493→    return true;
  1494→}
  1495→
  1496→function redo() {
