  1511→        version: SPLICE_VERSION,
  1512→        hasActiveSequence: app.project.activeSequence ? true : false,
  1513→        sequenceName: app.project.activeSequence ? app.project.activeSequence.name : null,
  1514→        numSequences: app.project.sequences.numSequences,
  1515→        numProjectItems: app.project.rootItem.children.numItems
