  1595→
  1596→    try {
  1597→        const response = await fetchWithTimeout(
  1598→            `${getBackendUrl()}/license/activate`,
  1599→            {
  1600→                method: 'POST',
  1601→                headers: { 'Content-Type': 'application/json' },
  1602→                body: JSON.stringify({ key: licenseKey })
  1603→            }
  1604→        );
  1605→
  1606→        if (!response.ok) {
  1607→            throw new Error(await parseErrorResponse(response));
  1608→        }
  1609→
  1610→        const data = await response.json();
  1611→        saveSettings({
  1612→            customerId: data.customerId,
  1613→            licenseKey: licenseKey
  1614→        });
  1615→
  1616→        if (ui.loginModal) ui.loginModal.classList.add('hidden');
  1617→        setStatus('License activated');
  1618→        updateCredits();
