  1627→async function lookupLicense() {
  1628→    const lookupEmailInput = document.getElementById('lookupEmailInput');
  1629→    const licenseKeyInput = document.getElementById('licenseKeyInput');
  1630→
  1631→    if (!lookupEmailInput) return;
  1632→
  1633→    const email = lookupEmailInput.value.trim().toLowerCase();
  1634→    if (!email) {
  1635→        setStatus('Please enter your email address', true);
  1636→        return;
  1637→    }
  1638→
  1639→    // Basic email validation
  1640→    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  1641→    if (!emailRegex.test(email)) {
  1642→        setStatus('Please enter a valid email address', true);
  1643→        return;
  1644→    }
  1645→
  1646→    const lookupBtn = document.getElementById('lookupLicenseBtn');
  1647→    if (lookupBtn) {
  1648→        lookupBtn.disabled = true;
  1649→        lookupBtn.textContent = 'Looking up...';
  1650→    }
  1651→
  1652→    try {
  1653→        const response = await fetchWithTimeout(
  1654→            `${getBackendUrl()}/license/lookup`,
  1655→            {
  1656→                method: 'POST',
  1657→                headers: { 'Content-Type': 'application/json' },
  1658→                body: JSON.stringify({ email })
  1659→            },
  1660→            30000
  1661→        );
  1662→
  1663→        const result = await response.json();
  1664→
  1665→        // Note: For security, the backend does NOT return the license key directly.
  1666→        // It sends the key via email to prevent enumeration attacks.
  1667→        if (result.success) {
  1668→            setStatus(result.message || 'If a license exists, it has been sent to your email.');
  1669→        } else {
  1670→            setStatus(result.error || 'Lookup failed. Please try again.', true);
  1671→        }
  1672→    } catch (error) {
  1673→        setStatus('Lookup failed: ' + error.message, true);
  1674→    } finally {
  1675→        if (lookupBtn) {
  1676→            lookupBtn.disabled = false;
  1677→            lookupBtn.textContent = 'Lookup';
  1678→        }
  1679→    }
  1680→}
