  1791→async function runDiagnostics() {
  1792→    if (!ui.debugLogs || !ui.debugQuickDiagnosis) return;
  1793→
  1794→    diagnosticLogs = [];
  1795→    ui.debugLogs.innerHTML = '<p class="debug-running">Running diagnostics... Please wait...</p>';
  1796→
  1797→    try {
  1798→        // Test 0: Environment Info (using compatibility module)
  1799→        addDiagnosticLog('=== Environment Detection ===');
  1800→        if (typeof SpliceCompatibility !== 'undefined') {
  1801→            const env = SpliceCompatibility.detectEnvironment();
  1802→            addDiagnosticLog('Platform: ' + env.platform.os + ' ' + env.platform.osVersion);
  1803→            addDiagnosticLog('Architecture: ' + env.platform.architecture + (env.platform.isAppleSilicon ? ' (Apple Silicon)' : ''));
  1804→            if (env.cep.isRunning) {
  1805→                addDiagnosticLog('CEP Version: ' + env.cep.version);
  1806→                addDiagnosticLog('Host: ' + env.host.name + ' ' + env.host.version);
  1807→            }
  1808→            addDiagnosticLog('Chromium: ' + (env.browser.chromiumVersion || 'N/A'));
  1809→        } else {
  1810→            addDiagnosticLog('Compatibility module not loaded');
  1811→        }
  1812→        addDiagnosticLog('');
  1813→
  1814→        // Test 0.1: Check CEP environment
  1815→        addDiagnosticLog('Test 0: Checking CEP environment...');
  1816→        if (window.__adobe_cep__) {
  1817→            addDiagnosticLog('✓ CEP runtime detected (window.__adobe_cep__ exists)');
  1818→        } else {
  1819→            addDiagnosticLog('✗ CEP runtime NOT detected - running in browser mode');
  1820→        }
  1821→
  1822→        // Test 0.5: Basic ExtendScript test (doesn't require hostScript.jsx)
  1823→        addDiagnosticLog('Test 0.5: Testing basic ExtendScript...');
  1824→        try {
  1825→            const basicResult = await jsx.evalScript('1 + 1');
  1826→            addDiagnosticLog('✓ Basic ExtendScript works: 1+1 = ' + basicResult);
  1827→        } catch (e) {
