  1928→        }
  1929→
  1930→        // Test 6: Check QE DOM availability
  1931→        addDiagnosticLog('Test 6: Checking QE DOM availability...');
  1932→        try {
  1933→            const qeResult = await jsx.evalScript(`
  1934→                (function() {
  1935→                    try {
  1936→                        if (typeof qe !== 'undefined' && qe !== null) {
  1937→                            return JSON.stringify({ available: true });
  1938→                        } else {
  1939→                            return JSON.stringify({ available: false });
  1940→                        }
  1941→                    } catch (e) {
  1942→                        return JSON.stringify({ available: false, error: e.message });
  1943→                    }
  1944→                })()
  1945→            `);
  1946→            const qeData = JSON.parse(qeResult);
  1947→            if (qeData.available) {
  1948→                addDiagnosticLog('✓ QE DOM is available');
  1949→            } else {
  1950→                addDiagnosticLog('✗ QE DOM is NOT available' + (qeData.error ? ': ' + qeData.error : ''));
  1951→            }
  1952→        } catch (e) {
  1953→            addDiagnosticLog('✗ QE DOM check FAILED: ' + e.message);
  1954→        }
  1955→
  1956→        // Test 7: Compatibility Check
  1957→        addDiagnosticLog('');
