    // Vlog/YouTube - punchy edits, engagement-focused
    vlog: {
        name: 'Vlog',
        description: 'Punchy edits for YouTube engagement',
        icon: 'videocam',
        settings: {
            sensitivity: 65,
            threshold: -30,
            minSilenceLength: 0.35,
            paddingStart: 0.08,
