  1214→
  1215→        // Queue the export
  1216→        encoder.encodeSequence(
  1217→            seq,
  1218→            outputPath,
  1219→            presetPath,
  1220→            0, // WorkAreaType: 0 = entire, 1 = work area
  1221→            false // removeOnCompletion
  1222→        );
  1223→
  1224→        return JSON.stringify({ success: true, outputPath: outputPath });
  1225→    } catch (e) {
