  1268→                var presetFiles = [
  1269→                    new File(appDataFolder.absoluteURI + "/Adobe/Common/AME/15.0/Presets"),
  1270→                    new File(appDataFolder.absoluteURI + "/Adobe/Common/AME/14.0/Presets"),
  1271→                    new File(appFolder.absoluteURI + "/MediaIO/systempresets/58534430_4d756c74-6962697400000000/WAV 48kHz.epr")
  1272→                ];
  1273→
  1274→                for (var i = 0; i < presetFiles.length; i++) {
