  1358→
  1359→function moveClipByNTracks(currentTrackId, clipStartTime, nTracksToMove, trackType) {
  1360→    if (!isQEAvailable() && !enableQE()) {
  1361→        return JSON.stringify({ error: "QE not available for moving clips between tracks" });
  1362→    }
