            var endTime = new Time();
            endTime.seconds = marker.start.seconds + duration;
            marker.end = endTime;
        }

        return JSON.stringify({
            success: true,
            guid: marker.guid
        });
    } catch (e) {
        return JSON.stringify({ error: e.message });
    }
}

