/**
 * SPLICE CEP Panel - Compatibility Detection Module
 * v6.0.8
 *
 * Detects runtime environment and provides feature detection utilities.
 * Uses feature detection instead of hardware detection for reliability.
 */

// ============================================================================
// COMPATIBILITY MODULE
// ============================================================================

const SpliceCompatibility = (function() {
    'use strict';

    // Cache for detected environment info
    let _envCache = null;
    let _featuresCache = null;
    let _initialized = false;

    // ========================================================================
    // ENVIRONMENT DETECTION
    // ========================================================================

    /**
     * Detect the runtime environment (CEP, macOS version, Premiere Pro version)
     * @returns {Object} Environment information
     */
    function detectEnvironment() {
        if (_envCache) {
            return _envCache;
        }

        const env = {
            // Timestamp
            detectedAt: new Date().toISOString(),

            // Platform info
            platform: {
                os: detectOS(),
                osVersion: detectOSVersion(),
                architecture: detectArchitecture(),
                isAppleSilicon: detectAppleSilicon(),
                userAgent: navigator.userAgent || 'unknown'
            },

            // CEP environment
            cep: {
                isRunning: isCEPEnvironment(),
                version: getCEPVersion(),
                hostApp: null,
                hostVersion: null,
                extensionId: null,
                locale: null
            },

            // Host application (Premiere Pro)
            host: {
                name: null,
                version: null,
                versionMajor: null,
                versionMinor: null,
                isOffline: false
            },

            // Browser/Chromium info
            browser: {
                chromiumVersion: getChromiumVersion(),
                v8Version: getV8Version(),
                supportsES6: supportsES6(),
                supportsAsyncAwait: supportsAsyncAwait()
            }
        };

        // Detect CEP host environment if available
        if (env.cep.isRunning) {
            try {
                const cs = new CSInterface();
                const hostEnv = cs.getHostEnvironment();

                if (hostEnv) {
                    env.cep.hostApp = hostEnv.appName || null;
                    env.cep.hostVersion = hostEnv.appVersion || null;
                    env.host.name = getHostAppName(hostEnv.appName);
                    env.host.version = hostEnv.appVersion || null;
                    env.host.isOffline = hostEnv.isAppOffline || false;
                    env.cep.locale = hostEnv.appLocale || null;

                    // Parse version numbers
                    if (hostEnv.appVersion) {
                        const versionParts = hostEnv.appVersion.split('.');
                        env.host.versionMajor = parseInt(versionParts[0]) || 0;
                        env.host.versionMinor = parseInt(versionParts[1]) || 0;
                    }
                }

                env.cep.extensionId = cs.getExtensionId() || 'com.splice.panel.main';
            } catch (e) {
                debugLog('[Compatibility] Error getting host environment:', e.message);
            }
        }

        _envCache = env;
        return env;
    }

    /**
     * Detect operating system
     * @returns {string} OS name
     */
    function detectOS() {
        const ua = navigator.userAgent || '';
        const platform = navigator.platform || '';

        if (platform.indexOf('Mac') !== -1 || ua.indexOf('Mac') !== -1) {
            return 'macOS';
        } else if (platform.indexOf('Win') !== -1 || ua.indexOf('Windows') !== -1) {
            return 'Windows';
        } else if (platform.indexOf('Linux') !== -1 || ua.indexOf('Linux') !== -1) {
            return 'Linux';
        }
        return 'unknown';
    }

    /**
     * Detect OS version
     * @returns {string} OS version string
     */
    function detectOSVersion() {
        const ua = navigator.userAgent || '';

        // macOS version detection
        const macMatch = ua.match(/Mac OS X (\d+[._]\d+[._]?\d*)/);
        if (macMatch) {
            return macMatch[1].replace(/_/g, '.');
        }

        // Windows version detection
        const winMatch = ua.match(/Windows NT (\d+\.\d+)/);
        if (winMatch) {
            const ntVersion = winMatch[1];
            // Map NT versions to Windows versions
            const ntMap = {
                '10.0': '10/11',
                '6.3': '8.1',
                '6.2': '8',
                '6.1': '7',
                '6.0': 'Vista'
            };
            return ntMap[ntVersion] || ntVersion;
        }

        return 'unknown';
    }

    /**
     * Detect CPU architecture
     * Note: This is limited in browser environments
     * @returns {string} Architecture string
     */
    function detectArchitecture() {
        // Check navigator.platform for architecture hints
        const platform = navigator.platform || '';
        const ua = navigator.userAgent || '';

        // Check for ARM64 indicators
        if (ua.indexOf('arm64') !== -1 || ua.indexOf('ARM64') !== -1) {
            return 'arm64';
        }

        // macOS on Apple Silicon may not always show arm64 in UA
        // but we can infer from other factors
        if (platform === 'MacIntel') {
            // Modern Safari/Chromium on Apple Silicon may still report 'MacIntel'
            // for compatibility, so we need to check other signals
            return detectAppleSilicon() ? 'arm64' : 'x86_64';
        }

        if (platform.indexOf('64') !== -1 || ua.indexOf('x86_64') !== -1 || ua.indexOf('x64') !== -1) {
            return 'x86_64';
        }

        if (platform.indexOf('Win32') !== -1 && ua.indexOf('WOW64') !== -1) {
            return 'x86_64'; // 32-bit browser on 64-bit Windows
        }

        return 'unknown';
    }

    /**
     * Detect if running on Apple Silicon (M1/M2/M3/M4)
     * Uses multiple heuristics since direct detection is limited
     * @returns {boolean} True if likely Apple Silicon
     */
    function detectAppleSilicon() {
        // Check for explicit arm64 indicator
        const ua = navigator.userAgent || '';
        if (ua.indexOf('arm64') !== -1 || ua.indexOf('ARM64') !== -1) {
            return true;
        }

        // macOS-specific checks
        if (detectOS() !== 'macOS') {
            return false;
        }

        // Method 1: Check for GL_RENDERER containing Apple GPU names
        // (Only works if WebGL is available and exposes this info)
        try {
            const canvas = document.createElement('canvas');
            const gl = canvas.getContext('webgl') || canvas.getContext('experimental-webgl');
            if (gl) {
                const debugInfo = gl.getExtension('WEBGL_debug_renderer_info');
                if (debugInfo) {
                    const renderer = gl.getParameter(debugInfo.UNMASKED_RENDERER_WEBGL);
                    // Apple Silicon GPUs contain "Apple" in the renderer name
                    if (renderer && renderer.indexOf('Apple') !== -1) {
                        // Check for M-series chip indicators
                        // Apple Silicon integrated GPUs are named like "Apple M1", "Apple M2", etc.
                        if (/Apple M\d/.test(renderer)) {
                            return true;
                        }
                    }
                }
            }
        } catch (e) {
            // WebGL detection failed, continue with other methods
        }

        // Method 2: Check macOS version (Apple Silicon requires macOS 11+)
        const osVersion = detectOSVersion();
        const majorVersion = parseInt(osVersion.split('.')[0]) || 0;

        // macOS 11 (Big Sur) and later are required for Apple Silicon
        // However, this is not definitive - Intel Macs can also run Big Sur+
        // So this is just a prerequisite check
        if (majorVersion < 11) {
            return false;
        }

        // Method 3: Performance heuristics (Apple Silicon has specific characteristics)
        // This is a soft indicator and not definitive
        // Apple Silicon typically has high core counts and specific timing patterns
        // However, we avoid this as it's unreliable

        // Default to unknown/false for Intel detection
        // We can't definitively detect Apple Silicon without system access
        return false;
    }

    /**
     * Check if running in CEP environment
     * @returns {boolean}
     */
    function isCEPEnvironment() {
        return typeof window.__adobe_cep__ !== 'undefined' && window.__adobe_cep__ !== null;
    }

    /**
     * Get CEP version
     * @returns {string|null}
     */
    function getCEPVersion() {
        if (!isCEPEnvironment()) {
            return null;
        }

        try {
            // CEP version is typically exposed through various means
            if (window.__adobe_cep__.getHostEnvironment) {
                const env = JSON.parse(window.__adobe_cep__.getHostEnvironment());
                if (env && env.CEPVersion) {
                    return env.CEPVersion;
                }
            }
        } catch (e) {
            // Ignore errors
        }

        // Default to CEP 11 based on manifest
        return '11.0';
    }

    /**
     * Get human-readable host application name
     * @param {string} appCode - Adobe app code (e.g., 'PPRO')
     * @returns {string}
     */
    function getHostAppName(appCode) {
        const appNames = {
            'PPRO': 'Adobe Premiere Pro',
            'AEFT': 'Adobe After Effects',
            'AUDT': 'Adobe Audition',
            'PHXS': 'Adobe Photoshop',
            'ILST': 'Adobe Illustrator',
            'IDSN': 'Adobe InDesign'
        };
        return appNames[appCode] || appCode || 'Unknown';
    }

    /**
     * Get Chromium version from user agent
     * @returns {string|null}
     */
    function getChromiumVersion() {
        const match = (navigator.userAgent || '').match(/Chrome\/(\d+\.\d+\.\d+\.\d+)/);
        return match ? match[1] : null;
    }

    /**
     * Get V8 JavaScript engine version
     * @returns {string|null}
     */
    function getV8Version() {
        // V8 version detection is limited in browsers
        // CEP uses Chromium Embedded Framework (CEF)
        const chromiumVersion = getChromiumVersion();
        if (chromiumVersion) {
            // V8 version roughly correlates with Chromium version
            return 'CEF/' + chromiumVersion;
        }
        return null;
    }

    /**
     * Check ES6 support
     * @returns {boolean}
     */
    function supportsES6() {
        try {
            // Test for arrow functions, const, let, template literals
            new Function('const x = 1; let y = 2; const f = () => `${x}${y}`; return f();')();
            return true;
        } catch (e) {
            return false;
        }
    }

    /**
     * Check async/await support
     * @returns {boolean}
     */
    function supportsAsyncAwait() {
        try {
            new Function('async function f() { await Promise.resolve(); }')();
            return true;
        } catch (e) {
            return false;
        }
    }

    // ========================================================================
    // FEATURE DETECTION
    // ========================================================================

    /**
     * Detect available features and capabilities
     * Uses feature detection instead of hardware detection for reliability
     * @returns {Object} Feature availability
     */
    function detectFeatures() {
        if (_featuresCache) {
            return _featuresCache;
        }

        const features = {
            // Web APIs
            webAPIs: {
                fetch: typeof fetch === 'function',
                promises: typeof Promise === 'function',
                webCrypto: typeof crypto !== 'undefined' && typeof crypto.subtle !== 'undefined',
                localStorage: hasLocalStorage(),
                sessionStorage: hasSessionStorage(),
                indexedDB: typeof indexedDB !== 'undefined',
                webWorkers: typeof Worker === 'function',
                webGL: hasWebGL(),
                webGL2: hasWebGL2(),
                canvas: hasCanvas(),
                audioContext: hasAudioContext(),
                mediaDevices: typeof navigator.mediaDevices !== 'undefined',
                clipboard: typeof navigator.clipboard !== 'undefined',
                notifications: typeof Notification !== 'undefined',
                serviceWorkers: 'serviceWorker' in navigator,
                webSockets: typeof WebSocket === 'function'
            },

            // JavaScript features
            javascript: {
                es6: supportsES6(),
                asyncAwait: supportsAsyncAwait(),
                generators: supportsGenerators(),
                symbols: typeof Symbol === 'function',
                maps: typeof Map === 'function',
                sets: typeof Set === 'function',
                weakMaps: typeof WeakMap === 'function',
                proxies: typeof Proxy === 'function',
                reflect: typeof Reflect !== 'undefined',
                bigInt: typeof BigInt === 'function'
            },

            // CEP-specific features
            cep: {
                evalScript: hasCEPEvalScript(),
                fileSystem: hasCEPFileSystem(),
                nodeJS: hasCEPNodeJS(),
                vulcanMessage: hasCEPVulcanMessage(),
                flyoutMenu: hasCEPFlyoutMenu()
            },

            // Premiere Pro features (detected via JSX)
            premiere: {
                detected: false,
                activeSequence: false,
                markers: false,
                exportMedia: false,
                qeDom: false
            }
        };

        _featuresCache = features;
        return features;
    }

    /**
     * Check localStorage availability
     * @returns {boolean}
     */
    function hasLocalStorage() {
        try {
            const test = '__splice_test__';
            localStorage.setItem(test, test);
            localStorage.removeItem(test);
            return true;
        } catch (e) {
            return false;
        }
    }

    /**
     * Check sessionStorage availability
     * @returns {boolean}
     */
    function hasSessionStorage() {
        try {
            const test = '__splice_test__';
            sessionStorage.setItem(test, test);
            sessionStorage.removeItem(test);
            return true;
        } catch (e) {
            return false;
        }
    }

    /**
     * Check WebGL availability
     * @returns {boolean}
     */
    function hasWebGL() {
        try {
            const canvas = document.createElement('canvas');
            return !!(canvas.getContext('webgl') || canvas.getContext('experimental-webgl'));
        } catch (e) {
            return false;
        }
    }

    /**
     * Check WebGL 2 availability
     * @returns {boolean}
     */
    function hasWebGL2() {
        try {
            const canvas = document.createElement('canvas');
            return !!canvas.getContext('webgl2');
        } catch (e) {
            return false;
        }
    }

    /**
     * Check canvas availability
     * @returns {boolean}
     */
    function hasCanvas() {
        try {
            const canvas = document.createElement('canvas');
            return !!canvas.getContext('2d');
        } catch (e) {
            return false;
        }
    }

    /**
     * Check AudioContext availability
     * @returns {boolean}
     */
    function hasAudioContext() {
        return typeof AudioContext !== 'undefined' || typeof webkitAudioContext !== 'undefined';
    }

    /**
     * Check generator function support
     * @returns {boolean}
     */
    function supportsGenerators() {
        try {
            new Function('function* f() { yield 1; }')();
            return true;
        } catch (e) {
            return false;
        }
    }

    /**
     * Check CEP evalScript availability
     * @returns {boolean}
     */
    function hasCEPEvalScript() {
        return isCEPEnvironment() && typeof window.__adobe_cep__.evalScript === 'function';
    }

    /**
     * Check CEP file system access
     * @returns {boolean}
     */
    function hasCEPFileSystem() {
        return isCEPEnvironment() && typeof window.cep !== 'undefined' &&
               typeof window.cep.fs !== 'undefined';
    }

    /**
     * Check CEP Node.js availability
     * @returns {boolean}
     */
    function hasCEPNodeJS() {
        return typeof process !== 'undefined' && typeof require === 'function';
    }

    /**
     * Check CEP Vulcan messaging
     * @returns {boolean}
     */
    function hasCEPVulcanMessage() {
        return typeof VulcanInterface !== 'undefined';
    }

    /**
     * Check CEP flyout menu
     * @returns {boolean}
     */
    function hasCEPFlyoutMenu() {
        if (!isCEPEnvironment()) return false;
        try {
            const cs = new CSInterface();
            return typeof cs.setPanelFlyoutMenu === 'function';
        } catch (e) {
            return false;
        }
    }

    // ========================================================================
    // PREMIERE PRO FEATURE DETECTION (ASYNC)
    // ========================================================================

    /**
     * Detect Premiere Pro features via JSX
     * Must be called after JSX bridge is initialized
     * @returns {Promise<Object>} Premiere features
     */
    async function detectPremiereFeatures() {
        if (!window.jsx || !window.jsx.cs) {
            debugLog('[Compatibility] JSX bridge not available for Premiere detection');
            return _featuresCache?.premiere || {};
        }

        const features = {
            detected: true,
            activeSequence: false,
            markers: false,
            exportMedia: false,
            qeDom: false
        };

        try {
            // Check for active sequence support
            const seqResult = await window.jsx.evalScript('typeof app.project.activeSequence !== "undefined"');
            features.activeSequence = seqResult === 'true';
        } catch (e) {
            debugLog('[Compatibility] Failed to detect activeSequence:', e.message);
        }

        try {
            // Check for markers support
            const markersResult = await window.jsx.evalScript(
                '(function() { try { return typeof app.project.activeSequence.markers !== "undefined"; } catch(e) { return false; } })()'
            );
            features.markers = markersResult === 'true';
        } catch (e) {
            debugLog('[Compatibility] Failed to detect markers:', e.message);
        }

        try {
            // Check for QE DOM availability
            const qeResult = await window.jsx.evalScript('typeof qe !== "undefined"');
            features.qeDom = qeResult === 'true';
        } catch (e) {
            debugLog('[Compatibility] Failed to detect QE DOM:', e.message);
        }

        try {
            // Check for export media encoder
            const exportResult = await window.jsx.evalScript('typeof app.encoder !== "undefined"');
            features.exportMedia = exportResult === 'true';
        } catch (e) {
            debugLog('[Compatibility] Failed to detect export media:', e.message);
        }

        // Update cache
        if (_featuresCache) {
            _featuresCache.premiere = features;
        }

        return features;
    }

    // ========================================================================
    // COMPATIBILITY CHECKS
    // ========================================================================

    /**
     * Check if environment meets minimum requirements
     * @returns {Object} Compatibility status with details
     */
    function checkCompatibility() {
        const env = detectEnvironment();
        const features = detectFeatures();

        const result = {
            compatible: true,
            warnings: [],
            errors: [],
            recommendations: []
        };

        // Check CEP environment
        if (!env.cep.isRunning) {
            result.warnings.push({
                code: 'NO_CEP',
                message: 'Not running in CEP environment. Some features will be unavailable.',
                severity: 'warning'
            });
        }

        // Check Premiere Pro version
        if (env.host.versionMajor && env.host.versionMajor < 22) {
            result.errors.push({
                code: 'PPRO_VERSION_LOW',
                message: `Premiere Pro version ${env.host.version} is not supported. Minimum required: 22.0 (2022)`,
                severity: 'error'
            });
            result.compatible = false;
        }

        // Check for required web APIs
        if (!features.webAPIs.fetch) {
            result.errors.push({
                code: 'NO_FETCH',
                message: 'Fetch API not available. Network operations will fail.',
                severity: 'error'
            });
            result.compatible = false;
        }

        if (!features.webAPIs.promises) {
            result.errors.push({
                code: 'NO_PROMISES',
                message: 'Promise API not available. Async operations will fail.',
                severity: 'error'
            });
            result.compatible = false;
        }

        if (!features.webAPIs.localStorage) {
            result.warnings.push({
                code: 'NO_LOCALSTORAGE',
                message: 'localStorage not available. Settings will not persist.',
                severity: 'warning'
            });
        }

        // Check JavaScript features
        if (!features.javascript.asyncAwait) {
            result.warnings.push({
                code: 'NO_ASYNC_AWAIT',
                message: 'async/await not supported. Some features may not work.',
                severity: 'warning'
            });
        }

        // Architecture-specific recommendations
        if (env.platform.isAppleSilicon) {
            result.recommendations.push({
                code: 'APPLE_SILICON',
                message: 'Apple Silicon detected. Ensure Premiere Pro is running natively (not via Rosetta) for best performance.'
            });
        }

        // CEP feature warnings
        if (!features.cep.evalScript) {
            result.warnings.push({
                code: 'NO_EVALSCRIPT',
                message: 'CEP evalScript not available. Cannot communicate with Premiere Pro.',
                severity: 'warning'
            });
        }

        return result;
    }

    // ========================================================================
    // LOGGING AND INITIALIZATION
    // ========================================================================

    /**
     * Log environment information to console
     * Called during extension initialization
     */
    function logEnvironmentInfo() {
        const env = detectEnvironment();
        const features = detectFeatures();

        console.log('[SPLICE] ==================== Environment Info ====================');
        console.log('[SPLICE] Platform:', env.platform.os, env.platform.osVersion);
        console.log('[SPLICE] Architecture:', env.platform.architecture);
        console.log('[SPLICE] Apple Silicon:', env.platform.isAppleSilicon ? 'Yes' : 'No');
        console.log('[SPLICE] CEP Runtime:', env.cep.isRunning ? 'Yes' : 'No (Browser Mode)');

        if (env.cep.isRunning) {
            console.log('[SPLICE] CEP Version:', env.cep.version);
            console.log('[SPLICE] Host App:', env.host.name, env.host.version);
            console.log('[SPLICE] Extension ID:', env.cep.extensionId);
            console.log('[SPLICE] Locale:', env.cep.locale);
        }

        console.log('[SPLICE] Chromium:', env.browser.chromiumVersion || 'N/A');
        console.log('[SPLICE] ES6 Support:', features.javascript.es6 ? 'Yes' : 'No');
        console.log('[SPLICE] Async/Await:', features.javascript.asyncAwait ? 'Yes' : 'No');
        console.log('[SPLICE] Web Crypto:', features.webAPIs.webCrypto ? 'Yes' : 'No');
        console.log('[SPLICE] localStorage:', features.webAPIs.localStorage ? 'Yes' : 'No');
        console.log('[SPLICE] ===============================================================');
    }

    /**
     * Initialize the compatibility module
     * Should be called early in the extension startup
     * @returns {Object} Initialization result
     */
    function init() {
        if (_initialized) {
            return { success: true, cached: true };
        }

        try {
            // Detect environment and features
            const env = detectEnvironment();
            const features = detectFeatures();
            const compatibility = checkCompatibility();

            // Log environment info
            logEnvironmentInfo();

            // Log compatibility issues
            if (compatibility.errors.length > 0) {
                console.error('[SPLICE] Compatibility errors detected:');
                compatibility.errors.forEach(err => {
                    console.error(`[SPLICE]   - ${err.code}: ${err.message}`);
                });
            }

            if (compatibility.warnings.length > 0) {
                console.warn('[SPLICE] Compatibility warnings:');
                compatibility.warnings.forEach(warn => {
                    console.warn(`[SPLICE]   - ${warn.code}: ${warn.message}`);
                });
            }

            if (compatibility.recommendations.length > 0) {
                console.log('[SPLICE] Recommendations:');
                compatibility.recommendations.forEach(rec => {
                    console.log(`[SPLICE]   - ${rec.message}`);
                });
            }

            _initialized = true;

            return {
                success: true,
                compatible: compatibility.compatible,
                environment: env,
                features: features,
                compatibility: compatibility
            };
        } catch (e) {
            console.error('[SPLICE] Compatibility detection failed:', e.message);
            return {
                success: false,
                error: e.message,
                compatible: true // Assume compatible if detection fails
            };
        }
    }

    // ========================================================================
    // UTILITY FUNCTIONS
    // ========================================================================

    /**
     * Get a summary string for diagnostics
     * @returns {string}
     */
    function getSummary() {
        const env = detectEnvironment();
        const parts = [];

        parts.push(`OS: ${env.platform.os} ${env.platform.osVersion}`);
        parts.push(`Arch: ${env.platform.architecture}`);

        if (env.platform.isAppleSilicon) {
            parts.push('(Apple Silicon)');
        }

        if (env.cep.isRunning) {
            parts.push(`CEP: ${env.cep.version}`);
            parts.push(`Host: ${env.host.name} ${env.host.version}`);
        } else {
            parts.push('Mode: Browser');
        }

        return parts.join(' | ');
    }

    /**
     * Check if a specific feature is available
     * @param {string} category - Feature category (webAPIs, javascript, cep)
     * @param {string} feature - Feature name
     * @returns {boolean}
     */
    function hasFeature(category, feature) {
        const features = detectFeatures();
        return features[category] && features[category][feature] === true;
    }

    /**
     * Get Premiere Pro version as comparable number
     * @returns {number} Version number (e.g., 25.1 -> 2501)
     */
    function getPremiereVersionNumber() {
        const env = detectEnvironment();
        if (!env.host.versionMajor) return 0;
        return (env.host.versionMajor * 100) + (env.host.versionMinor || 0);
    }

    /**
     * Check if running on minimum Premiere Pro version
     * @param {number} majorVersion - Minimum major version
     * @param {number} minorVersion - Minimum minor version (optional)
     * @returns {boolean}
     */
    function isPremiereVersionAtLeast(majorVersion, minorVersion = 0) {
        const required = (majorVersion * 100) + minorVersion;
        return getPremiereVersionNumber() >= required;
    }

    // ========================================================================
    // PUBLIC API
    // ========================================================================

    return {
        // Initialization
        init: init,

        // Environment detection
        detectEnvironment: detectEnvironment,
        detectFeatures: detectFeatures,
        detectPremiereFeatures: detectPremiereFeatures,

        // Compatibility checking
        checkCompatibility: checkCompatibility,

        // Utility functions
        getSummary: getSummary,
        hasFeature: hasFeature,
        getPremiereVersionNumber: getPremiereVersionNumber,
        isPremiereVersionAtLeast: isPremiereVersionAtLeast,

        // Quick checks
        isCEPEnvironment: isCEPEnvironment,
        isAppleSilicon: function() { return detectEnvironment().platform.isAppleSilicon; },
        getOS: function() { return detectEnvironment().platform.os; },
        getOSVersion: function() { return detectEnvironment().platform.osVersion; },
        getArchitecture: function() { return detectEnvironment().platform.architecture; },
        getHostVersion: function() { return detectEnvironment().host.version; },

        // Logging
        logEnvironmentInfo: logEnvironmentInfo
    };
})();

// ============================================================================
// AUTO-INITIALIZATION
// ============================================================================

// Initialize compatibility detection on DOMContentLoaded
document.addEventListener('DOMContentLoaded', function() {
    const result = SpliceCompatibility.init();

    if (result.success) {
        debugLog('[Compatibility] Module initialized successfully');

        // Store in window for debugging
        window.spliceCompatibility = SpliceCompatibility;

        // Also make the summary available
        window.spliceEnvironmentSummary = SpliceCompatibility.getSummary();
    } else {
        console.error('[SPLICE] Compatibility module failed to initialize:', result.error);
    }
});

// ============================================================================
// EXPORTS
// ============================================================================

// Export to window for use by other modules
window.SpliceCompatibility = SpliceCompatibility;

