   566→          const priceId = subscription.items?.data?.[0]?.price?.id;
   567→          tier = getTierFromPriceId(priceId);
   568→
   569→          await usageTracking.resetHours(customerId, tier);
   570→          await usageTracking.resetMusicCredits(customerId, tier);
