   886→
   887→async function gracefulShutdown(signal) {
   888→  console.log(`[SPLICE] Received ${signal}, starting graceful shutdown...`);
   889→
   890→  // Stop accepting new connections
   891→  if (server) {
   892→    server.close(() => {
   893→      console.log('[SPLICE] HTTP server closed');
