  1007→        setStatus(`Found ${previewSilences.length} silences, ${previewTakes.length} takes`);
  1008→
  1009→    } catch (error) {
  1010→        console.error('[SPLICE] Detection error:', error);
  1011→        setStatus('Detection failed: ' + error.message, true);
