  1071→function renderPreviewList() {
  1072→    if (!ui.previewList) return;
  1073→
  1074→    const html = [];
  1075→
  1076→    // Render silences
  1077→    previewSilences.forEach((silence, i) => {
  1078→        const isSelected = selectedSilenceIndices.has(i);
  1079→        const duration = silence.end - silence.start;
  1080→        html.push(`
  1081→            <div class="preview-item ${isSelected ? '' : 'excluded'}" data-index="${i}" data-type="silence">
  1082→                <input type="checkbox" class="preview-item-check" ${isSelected ? 'checked' : ''}>
  1083→                <div class="preview-item-info">
  1084→                    <div class="preview-item-time">${formatTime(silence.start)} - ${formatTime(silence.end)}</div>
  1085→                    <div class="preview-item-duration">${duration.toFixed(2)}s silence</div>
  1086→                </div>
  1087→            </div>
  1088→        `);
  1089→    });
  1090→
  1091→    // Render takes
  1092→    previewTakes.forEach((take, i) => {
  1093→        // SECURITY: Escape take label to prevent XSS
  1094→        const safeLabel = escapeHtml(take.label || 'Take ' + (take.takeNumber || i + 1));
  1095→        html.push(`
  1096→            <div class="preview-item take-item" data-index="${i}" data-type="take">
  1097→                <div class="preview-item-icon">🎬</div>
  1098→                <div class="preview-item-info">
  1099→                    <div class="preview-item-time">${formatTime(take.start)} - ${formatTime(take.end)}</div>
  1100→                    <div class="preview-item-label">${safeLabel}</div>
  1101→                </div>
  1102→            </div>
  1103→        `);
  1104→    });
  1105→
  1106→    ui.previewList.innerHTML = html.join('');
  1107→
  1108→    // Add click handlers
  1109→    ui.previewList.querySelectorAll('.preview-item').forEach(item => {
  1110→        item.addEventListener('click', (e) => {
  1111→            const index = parseInt(item.dataset.index);
  1112→            const type = item.dataset.type;
  1113→
  1114→            if (e.target.classList.contains('preview-item-check')) {
  1115→                // Checkbox clicked
  1116→                toggleSilenceSelection(index);
  1117→            } else {
  1118→                // Item clicked - seek to time
  1119→                const data = type === 'silence' ? previewSilences[index] : previewTakes[index];
  1120→                seekToTime(data.start);
  1121→            }
  1122→        });
  1123→    });
  1124→}
