  1145→async function seekToTime(seconds) {
  1146→    try {
  1147→        // FIX: Removed invalid 'app' check - 'app' is ExtendScript DOM, not available in panel JS
  1148→        // The JSX evalScript will handle the error case if no sequence is open
  1149→
  1150→        // Use JSX to set playhead position
  1151→        // Note: setPlayerPosition expects ticks as a string without quotes around the number value
  1152→        const ticks = Math.round(seconds * 254016000000);
  1153→        const result = await jsx.evalScript(`
  1154→            (function() {
  1155→                try {
  1156→                    if (!app.project.activeSequence) {
  1157→                        return JSON.stringify({ error: 'No active sequence' });
  1158→                    }
  1159→                    app.project.activeSequence.setPlayerPosition('${ticks}');
  1160→                    return JSON.stringify({ success: true });
  1161→                } catch (e) {
  1162→                    return JSON.stringify({ error: e.message });
  1163→                }
  1164→            })()
  1165→        `);
  1166→
  1167→        // Check for errors
  1168→        if (result && typeof result === 'string') {
  1169→            try {
  1170→                const parsed = JSON.parse(result);
  1171→                if (parsed.error) {
  1172→                    debugLog('Seek failed:', parsed.error);
  1173→                }
  1174→            } catch (parseErr) {
  1175→                // Result wasn't JSON, ignore
  1176→            }
  1177→        }
  1178→    } catch (e) {
  1179→        console.warn('[SPLICE] Seek failed:', e);
  1180→    }
  1181→}
