  1514→/**
  1515→ * ACCESSIBILITY: Announce message to screen readers via ARIA live region
  1516→ * @param {string} message - Message to announce
  1517→ * @param {string} priority - 'polite' or 'assertive'
  1518→ */
  1519→function announceToScreenReader(message, priority = 'polite') {
  1520→    // Use existing status element or create temporary one
  1521→    const statusEl = document.getElementById('status');
  1522→    if (statusEl && priority === 'polite') {
  1523→        const originalText = statusEl.textContent;
  1524→        statusEl.textContent = message;
  1525→        // Restore after announcement
  1526→        setTimeout(() => {
  1527→            if (statusEl.textContent === message) {
  1528→                statusEl.textContent = originalText;
  1529→            }
  1530→        }, 1000);
  1531→        return;
  1532→    }
  1533→
  1534→    // Create temporary live region for assertive announcements
  1535→    const liveRegion = document.createElement('div');
  1536→    liveRegion.setAttribute('role', 'alert');
  1537→    liveRegion.setAttribute('aria-live', priority);
  1538→    liveRegion.setAttribute('aria-atomic', 'true');
  1539→    liveRegion.className = 'sr-only';
  1540→    liveRegion.style.cssText = 'position:absolute;width:1px;height:1px;padding:0;margin:-1px;overflow:hidden;clip:rect(0,0,0,0);border:0;';
  1541→    liveRegion.textContent = message;
  1542→    document.body.appendChild(liveRegion);
  1543→
  1544→    // Remove after announcement
  1545→    setTimeout(() => liveRegion.remove(), 1000);
  1546→}
