  1785→
  1786→// ============================================================================
  1787→// DIAGNOSTICS
  1788→// ============================================================================
  1789→let diagnosticLogs = [];
  1790→
  1791→async function runDiagnostics() {
  1792→    if (!ui.debugLogs || !ui.debugQuickDiagnosis) return;
  1793→
  1794→    diagnosticLogs = [];
  1795→    ui.debugLogs.innerHTML = '<p class="debug-running">Running diagnostics... Please wait...</p>';
  1796→
  1797→    try {
  1798→        // Test 0: Environment Info (using compatibility module)
  1799→        addDiagnosticLog('=== Environment Detection ===');
  1800→        if (typeof SpliceCompatibility !== 'undefined') {
  1801→            const env = SpliceCompatibility.detectEnvironment();
  1802→            addDiagnosticLog('Platform: ' + env.platform.os + ' ' + env.platform.osVersion);
  1803→            addDiagnosticLog('Architecture: ' + env.platform.architecture + (env.platform.isAppleSilicon ? ' (Apple Silicon)' : ''));
  1804→            if (env.cep.isRunning) {
  1805→                addDiagnosticLog('CEP Version: ' + env.cep.version);
  1806→                addDiagnosticLog('Host: ' + env.host.name + ' ' + env.host.version);
  1807→            }
  1808→            addDiagnosticLog('Chromium: ' + (env.browser.chromiumVersion || 'N/A'));
  1809→        } else {
  1810→            addDiagnosticLog('Compatibility module not loaded');
  1811→        }
  1812→        addDiagnosticLog('');
  1813→
  1814→        // Test 0.1: Check CEP environment
  1815→        addDiagnosticLog('Test 0: Checking CEP environment...');
  1816→        if (window.__adobe_cep__) {
  1817→            addDiagnosticLog('✓ CEP runtime detected (window.__adobe_cep__ exists)');
  1818→        } else {
  1819→            addDiagnosticLog('✗ CEP runtime NOT detected - running in browser mode');
  1820→        }
  1821→
  1822→        // Test 0.5: Basic ExtendScript test (doesn't require hostScript.jsx)
  1823→        addDiagnosticLog('Test 0.5: Testing basic ExtendScript...');
  1824→        try {
  1825→            const basicResult = await jsx.evalScript('1 + 1');
  1826→            addDiagnosticLog('✓ Basic ExtendScript works: 1+1 = ' + basicResult);
  1827→        } catch (e) {
  1828→            addDiagnosticLog('✗ Basic ExtendScript FAILED: ' + e.message);
  1829→            addDiagnosticLog('  → This means Premiere Pro cannot run ANY ExtendScript');
  1830→            addDiagnosticLog('  → Make sure CEP debug mode is enabled and Premiere was restarted');
  1831→        }
  1832→
  1833→        // Test 0.6: Check if hostScript.jsx functions are loaded
  1834→        addDiagnosticLog('Test 0.6: Checking if hostScript.jsx is loaded...');
  1835→        try {
  1836→            const typeofGetVersion = await jsx.evalScript('typeof getVersion');
  1837→            if (typeofGetVersion === 'function') {
  1838→                addDiagnosticLog('✓ hostScript.jsx is loaded (getVersion exists)');
  1839→            } else {
  1840→                addDiagnosticLog('✗ hostScript.jsx NOT loaded (getVersion type: ' + typeofGetVersion + ')');
  1841→                addDiagnosticLog('  → The JSX file may not be in the correct location');
  1842→            }
  1843→        } catch (e) {
  1844→            addDiagnosticLog('✗ hostScript check FAILED: ' + e.message);
  1845→        }
  1846→
  1847→        // Test 1: JSX Connection
  1848→        addDiagnosticLog('Test 1: Checking JSX connection...');
  1849→        try {
  1850→            const version = await jsx.call('getVersion');
  1851→            addDiagnosticLog('✓ JSX connection OK, version: ' + version);
  1852→        } catch (e) {
  1853→            addDiagnosticLog('✗ JSX connection FAILED: ' + e.message);
  1854→        }
  1855→
  1856→        // Test 2: Check for active sequence
  1857→        addDiagnosticLog('Test 2: Checking for active sequence...');
  1858→        try {
  1859→            const hasSequence = await checkSequenceOpen(0, 0); // No retries for diagnostics
  1860→            addDiagnosticLog('✓ checkSequenceOpen() returned: ' + hasSequence);
  1861→        } catch (e) {
  1862→            addDiagnosticLog('✗ checkSequenceOpen() FAILED: ' + e.message);
  1863→        }
  1864→
  1865→        // Test 3: Get sequence info
  1866→        addDiagnosticLog('Test 3: Getting sequence info...');
  1867→        try {
  1868→            const seqInfo = await jsx.call('getActiveSequence');
  1869→            if (seqInfo && !seqInfo.error) {
  1870→                addDiagnosticLog('✓ Sequence found: ' + (seqInfo.name || 'Unknown'));
  1871→                addDiagnosticLog('  - Sequence ID: ' + (seqInfo.sequenceID || 'N/A'));
  1872→                addDiagnosticLog('  - Duration: ' + (seqInfo.duration || 'N/A'));
  1873→                addDiagnosticLog('  - Frame size: ' + (seqInfo.frameSizeHorizontal || 'N/A') + 'x' + (seqInfo.frameSizeVertical || 'N/A'));
  1874→            } else {
  1875→                addDiagnosticLog('✗ No active sequence found');
  1876→            }
  1877→        } catch (e) {
  1878→            addDiagnosticLog('✗ getActiveSequence() FAILED: ' + e.message);
  1879→        }
  1880→
  1881→        // Test 4: Check project sequences
  1882→        addDiagnosticLog('Test 4: Checking all project sequences...');
  1883→        try {
  1884→            const sequences = await jsx.call('getAllSequences');
  1885→            if (sequences && sequences.length > 0) {
  1886→                addDiagnosticLog('✓ Total sequences in project: ' + sequences.length);
  1887→                sequences.forEach((seq, i) => {
  1888→                    addDiagnosticLog('  - Sequence ' + i + ': ' + (seq.name || 'Unnamed'));
  1889→                });
  1890→            } else {
  1891→                addDiagnosticLog('✗ No sequences found in project');
  1892→            }
  1893→        } catch (e) {
  1894→            addDiagnosticLog('✗ getAllSequences() FAILED: ' + e.message);
  1895→        }
  1896→
  1897→        // Test 5: Check for active sequence via DOM
  1898→        addDiagnosticLog('Test 5: Checking app.project.activeSequence...');
  1899→        try {
  1900→            const result = await jsx.evalScript(`
  1901→                (function() {
  1902→                    try {
  1903→                        if (app && app.project && app.project.activeSequence) {
  1904→                            return JSON.stringify({
  1905→                                success: true,
  1906→                                name: app.project.activeSequence.name,
  1907→                                id: app.project.activeSequence.sequenceID
  1908→                            });
  1909→                        } else {
  1910→                            return JSON.stringify({
  1911→                                success: false,
  1912→                                reason: app ? (app.project ? 'app.project.activeSequence is null' : 'app.project is null') : 'app is null'
  1913→                            });
  1914→                        }
  1915→                    } catch (e) {
  1916→                        return JSON.stringify({ success: false, reason: 'Error: ' + e.message });
  1917→                    }
  1918→                })()
  1919→            `);
  1920→            const data = JSON.parse(result);
  1921→            if (data.success) {
  1922→                addDiagnosticLog('✓ app.project.activeSequence found: ' + data.name + ' (ID: ' + data.id + ')');
  1923→            } else {
  1924→                addDiagnosticLog('✗ app.project.activeSequence NOT found: ' + data.reason);
  1925→            }
  1926→        } catch (e) {
  1927→            addDiagnosticLog('✗ DOM check FAILED: ' + e.message);
  1928→        }
  1929→
  1930→        // Test 6: Check QE DOM availability
  1931→        addDiagnosticLog('Test 6: Checking QE DOM availability...');
  1932→        try {
  1933→            const qeResult = await jsx.evalScript(`
  1934→                (function() {
  1935→                    try {
  1936→                        if (typeof qe !== 'undefined' && qe !== null) {
  1937→                            return JSON.stringify({ available: true });
  1938→                        } else {
  1939→                            return JSON.stringify({ available: false });
  1940→                        }
  1941→                    } catch (e) {
  1942→                        return JSON.stringify({ available: false, error: e.message });
  1943→                    }
  1944→                })()
  1945→            `);
  1946→            const qeData = JSON.parse(qeResult);
  1947→            if (qeData.available) {
  1948→                addDiagnosticLog('✓ QE DOM is available');
  1949→            } else {
  1950→                addDiagnosticLog('✗ QE DOM is NOT available' + (qeData.error ? ': ' + qeData.error : ''));
  1951→            }
  1952→        } catch (e) {
  1953→            addDiagnosticLog('✗ QE DOM check FAILED: ' + e.message);
  1954→        }
  1955→
  1956→        // Test 7: Compatibility Check
  1957→        addDiagnosticLog('');
  1958→        addDiagnosticLog('Test 7: Compatibility check...');
  1959→        if (typeof SpliceCompatibility !== 'undefined') {
  1960→            const compat = SpliceCompatibility.checkCompatibility();
  1961→            if (compat.compatible) {
  1962→                addDiagnosticLog('✓ Environment is compatible');
  1963→            } else {
  1964→                addDiagnosticLog('✗ Environment compatibility issues detected');
  1965→            }
  1966→            if (compat.errors.length > 0) {
  1967→                compat.errors.forEach(err => {
  1968→                    addDiagnosticLog('  ERROR: ' + err.message);
  1969→                });
  1970→            }
  1971→            if (compat.warnings.length > 0) {
  1972→                compat.warnings.forEach(warn => {
  1973→                    addDiagnosticLog('  WARNING: ' + warn.message);
  1974→                });
  1975→            }
  1976→            if (compat.recommendations.length > 0) {
  1977→                compat.recommendations.forEach(rec => {
  1978→                    addDiagnosticLog('  TIP: ' + rec.message);
  1979→                });
  1980→            }
  1981→
  1982→            // Feature detection summary
  1983→            const features = SpliceCompatibility.detectFeatures();
  1984→            addDiagnosticLog('');
  1985→            addDiagnosticLog('=== Feature Detection ===');
  1986→            addDiagnosticLog('Fetch API: ' + (features.webAPIs.fetch ? 'Yes' : 'No'));
  1987→            addDiagnosticLog('Web Crypto: ' + (features.webAPIs.webCrypto ? 'Yes' : 'No'));
  1988→            addDiagnosticLog('localStorage: ' + (features.webAPIs.localStorage ? 'Yes' : 'No'));
  1989→            addDiagnosticLog('CEP evalScript: ' + (features.cep.evalScript ? 'Yes' : 'No'));
  1990→            addDiagnosticLog('CEP Node.js: ' + (features.cep.nodeJS ? 'Yes' : 'No'));
  1991→        } else {
  1992→            addDiagnosticLog('✗ Compatibility module not available');
  1993→        }
  1994→
  1995→        // Display quick diagnosis
  1996→        updateQuickDiagnosis();
  1997→
  1998→        // Display all logs
  1999→        displayDiagnosticLogs();
  2000→
  2001→    } catch (error) {
  2002→        addDiagnosticLog('DIAGNOSTICS ERROR: ' + error.message);
  2003→        displayDiagnosticLogs();
  2004→    }
  2005→}
