  2007→function addDiagnosticLog(message) {
  2008→    diagnosticLogs.push(message);
  2009→    console.log('[SPLICE DEBUG] ' + message);
  2010→}
  2011→
  2012→function displayDiagnosticLogs() {
  2013→    if (!ui.debugLogs) return;
  2014→
  2015→    const html = diagnosticLogs.map(log => {
  2016→        return `<div class="debug-log-entry">${escapeHtml(log)}</div>`;
  2017→    }).join('');
  2018→
  2019→    ui.debugLogs.innerHTML = html;
