    if (ui.jcutLeadOut) {
        ui.jcutLeadOut.value = Math.round(settings.jcutLeadOut * 100);
        if (ui.jcutLeadOutValue) ui.jcutLeadOutValue.textContent = settings.jcutLeadOut + 's';
