     1→import { NextRequest, NextResponse } from "next/server";
     2→import Stripe from "stripe";
     3→import { csrfGuard } from "@/lib/csrf-server";
     4→
     5→// Force Node.js runtime (not Edge) for better Stripe compatibility
     6→export const runtime = "nodejs";
     7→export const maxDuration = 30;
     8→
     9→// Lazy initialization to avoid errors during build
    10→let stripe: Stripe | null = null;
    11→function getStripe(): Stripe {
    12→  if (!stripe) {
    13→    if (!process.env.STRIPE_SECRET_KEY) {
    14→      throw new Error("STRIPE_SECRET_KEY is not configured");
    15→    }
    16→    stripe = new Stripe(process.env.STRIPE_SECRET_KEY, {
    17→      timeout: 10000,
    18→      maxNetworkRetries: 2,
    19→    });
    20→  }
    21→  return stripe;
    22→}
    23→
    24→// Get price IDs at runtime to ensure env vars are loaded
    25→function getPriceIds() {
    26→  return {
    27→    monthly: {
    28→      starter: process.env.STRIPE_PRICE_STARTER || "",
    29→      pro: process.env.STRIPE_PRICE_PRO || "",
    30→      team: process.env.STRIPE_PRICE_TEAM || "",
    31→    },
    32→    annual: {
    33→      starter: process.env.STRIPE_PRICE_STARTER_ANNUAL || "",
    34→      pro: process.env.STRIPE_PRICE_PRO_ANNUAL || "",
    35→      team: process.env.STRIPE_PRICE_TEAM_ANNUAL || "",
    36→    },
    37→  };
    38→}
    39→
    40→export async function POST(request: NextRequest) {
    41→  // CSRF validation
    42→  const csrfError = await csrfGuard(request);
    43→  if (csrfError) {
    44→    console.warn("[Checkout] CSRF validation failed");
    45→    return csrfError;
    46→  }
    47→
    48→  try {
    49→    const body = await request.json();
    50→    const { plan, email, successUrl, cancelUrl, billing = "monthly" } = body;
    51→
    52→    // Get price IDs at runtime
    53→    const allPrices = getPriceIds();
    54→    const priceIds = billing === "annual" ? allPrices.annual : allPrices.monthly;
    55→
    56→    if (!plan || !priceIds[plan as keyof typeof priceIds]) {
    57→      return NextResponse.json(
    58→        { error: "Invalid plan selected" },
    59→        { status: 400 }
    60→      );
    61→    }
    62→
    63→    const priceId = priceIds[plan as keyof typeof priceIds];
    64→
    65→    if (!priceId) {
    66→      return NextResponse.json(
    67→        { error: "Price ID not configured for this plan" },
    68→        { status: 500 }
    69→      );
    70→    }
    71→
    72→    // Create Stripe checkout session using raw fetch (workaround for Vercel SDK connectivity issues)
    73→    const baseUrl = process.env.NEXT_PUBLIC_BASE_URL || "https://spliceclips.com";
    74→    const successUrlFinal = successUrl || `${baseUrl}/checkout/success`;
    75→    const cancelUrlFinal = cancelUrl || `${baseUrl}/pricing`;
    76→
    77→    const params = new URLSearchParams();
    78→    params.append("mode", "subscription");
    79→    params.append("payment_method_types[0]", "card");
    80→    params.append("line_items[0][price]", priceId);
    81→    params.append("line_items[0][quantity]", "1");
    82→    if (email) params.append("customer_email", email);
    83→    params.append("success_url", successUrlFinal);
    84→    params.append("cancel_url", cancelUrlFinal);
    85→    params.append("subscription_data[trial_period_days]", "14");
    86→    params.append("subscription_data[metadata][plan]", plan);
    87→    params.append("subscription_data[metadata][billing]", billing);
    88→    params.append("metadata[plan]", plan);
    89→    params.append("metadata[billing]", billing);
    90→    params.append("allow_promotion_codes", "true");
    91→    params.append("billing_address_collection", "required");
    92→
    93→    const response = await fetch("https://api.stripe.com/v1/checkout/sessions", {
    94→      method: "POST",
    95→      headers: {
    96→        "Authorization": `Bearer ${process.env.STRIPE_SECRET_KEY}`,
    97→        "Content-Type": "application/x-www-form-urlencoded",
    98→      },
    99→      body: params,
   100→    });
   101→
   102→    if (!response.ok) {
   103→      const errorData = await response.json();
   104→      console.error("[Checkout] Stripe API error:", errorData);
   105→      throw new Error(errorData.error?.message || "Stripe API error");
   106→    }
   107→
   108→    const session = await response.json();
   109→
   110→    return NextResponse.json({
   111→      sessionId: session.id,
   112→      url: session.url,
   113→    });
   114→  } catch (error) {
   115→    console.error("[Checkout] Error:", error instanceof Error ? error.message : error);
   116→    return NextResponse.json(
   117→      {
   118→        error:
   119→          error instanceof Error ? error.message : "Failed to create checkout session",
   120→      },
   121→      { status: 500 }
   122→    );
   123→  }
   124→}
   125→
   126→export async function GET(request: NextRequest) {
   127→  const searchParams = request.nextUrl.searchParams;
   128→  const sessionId = searchParams.get("session_id");
   129→
   130→  if (!sessionId) {
   131→    return NextResponse.json(
   132→      { error: "Session ID is required" },
   133→      { status: 400 }
   134→    );
   135→  }
   136→
   137→  try {
   138→    const session = await getStripe().checkout.sessions.retrieve(sessionId, {
   139→      expand: ["subscription", "customer"],
   140→    });
   141→
   142→    return NextResponse.json({
   143→      status: session.status,
   144→      customerEmail: session.customer_details?.email,
   145→      plan: session.metadata?.plan,
   146→      subscriptionId:
   147→        typeof session.subscription === "string"
   148→          ? session.subscription
   149→          : session.subscription?.id,
   150→    });
   151→  } catch (error) {
   152→    console.error("Session retrieval error:", error);
   153→    return NextResponse.json(
   154→      {
   155→        error:
   156→          error instanceof Error
   157→            ? error.message
   158→            : "Failed to retrieve session",
   159→      },
   160→      { status: 500 }
   161→    );
   162→  }
   163→}
