import { NextRequest, NextResponse } from "next/server";
import { cookies } from "next/headers";
import { csrfGuard } from "@/lib/csrf-server";

// Backend API URL for authentication
const BACKEND_API_URL =
  process.env.BACKEND_API_URL || "https://api.spliceclips.com";

const isProduction = process.env.NODE_ENV === "production";
const ACCESS_TOKEN_MAX_AGE = 60 * 60; // 1 hour in seconds

export async function POST(request: NextRequest) {
  // CSRF validation
  const csrfError = await csrfGuard(request);
  if (csrfError) {
    console.warn("[Auth] Refresh CSRF validation failed");
    return csrfError;
  }

  try {
    const cookieStore = await cookies();
    const refreshToken = cookieStore.get("splice_refresh_token")?.value;

    if (!refreshToken) {
      return NextResponse.json(
        { error: "Refresh token is required", success: false },
        { status: 400 }
      );
    }

    // Forward the refresh request to the backend
    const response = await fetch(`${BACKEND_API_URL}/auth/refresh`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ refreshToken }),
    });

    const data = await response.json();

    if (!response.ok || !data.success) {
      // Clear auth cookies on refresh failure
      const errorResponse = NextResponse.json(
        {
          error: data.error || "Token refresh failed",
          message: data.message || "Please log in again",
          success: false,
        },
        { status: response.status }
      );

      // Clear cookies
      errorResponse.cookies.set("splice_token", "", {
        httpOnly: true,
        secure: isProduction,
        sameSite: "strict",
        path: "/",
        maxAge: 0,
      });
      errorResponse.cookies.set("splice_auth_status", "", {
        httpOnly: false,
        secure: isProduction,
        sameSite: "strict",
        path: "/",
        maxAge: 0,
      });
      errorResponse.cookies.set("splice_user", "", {
        httpOnly: false,
        secure: isProduction,
        sameSite: "strict",
        path: "/",
        maxAge: 0,
      });

      return errorResponse;
    }

    // Create response with success
    const jsonResponse = NextResponse.json({
      success: true,
      tokenType: data.tokenType,
      expiresIn: data.expiresIn,
    });

    // Update the access token cookie
    jsonResponse.cookies.set("splice_token", data.token, {
      httpOnly: true,
      secure: isProduction,
      sameSite: "strict",
      path: "/",
      maxAge: ACCESS_TOKEN_MAX_AGE,
    });

    // Update auth status cookie
    jsonResponse.cookies.set("splice_auth_status", "authenticated", {
      httpOnly: false,
      secure: isProduction,
      sameSite: "strict",
      path: "/",
      maxAge: ACCESS_TOKEN_MAX_AGE,
    });

    return jsonResponse;
  } catch (error) {
    console.error("[Auth] Refresh error:", error);
    return NextResponse.json(
      {
        error: "Token refresh failed",
        message: "Unable to refresh authentication. Please log in again.",
        success: false,
      },
      { status: 500 }
    );
  }
}

