import { NextRequest, NextResponse } from "next/server";
import { cookies } from "next/headers";
import { csrfGuard } from "@/lib/csrf-server";

// Backend API URL for authentication
const BACKEND_API_URL =
  process.env.BACKEND_API_URL || "https://api.spliceclips.com";

const isProduction = process.env.NODE_ENV === "production";

export async function POST(request: NextRequest) {
  // CSRF validation
  const csrfError = await csrfGuard(request);
  if (csrfError) {
    console.warn("[Auth] Logout CSRF validation failed");
    return csrfError;
  }

  try {
    const cookieStore = await cookies();
    const token = cookieStore.get("splice_token")?.value;
    const refreshToken = cookieStore.get("splice_refresh_token")?.value;

    // If we have tokens, notify the backend to blacklist them
    if (token) {
      try {
        await fetch(`${BACKEND_API_URL}/auth/logout`, {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify({ refreshToken }),
        });
      } catch (backendError) {
        // Log but don't fail - we still want to clear cookies
        console.error("[Auth] Backend logout error:", backendError);
      }
    }

    // Create response
    const response = NextResponse.json({
      success: true,
      message: "Logged out successfully",
    });

    // Clear all auth cookies by setting them with maxAge: 0
    response.cookies.set("splice_token", "", {
      httpOnly: true,
      secure: isProduction,
      sameSite: "strict",
      path: "/",
      maxAge: 0,
    });

    response.cookies.set("splice_refresh_token", "", {
      httpOnly: true,
      secure: isProduction,
      sameSite: "strict",
      path: "/",
      maxAge: 0,
    });

    response.cookies.set("splice_auth_status", "", {
      httpOnly: false,
      secure: isProduction,
      sameSite: "strict",
      path: "/",
      maxAge: 0,
    });

    response.cookies.set("splice_user", "", {
      httpOnly: false,
      secure: isProduction,
      sameSite: "strict",
      path: "/",
      maxAge: 0,
    });

    return response;
  } catch (error) {
    console.error("[Auth] Logout error:", error);

    // Even on error, try to clear cookies
    const response = NextResponse.json(
      {
        success: true,
        message: "Logged out locally",
      },
      { status: 200 }
    );

    // Clear cookies anyway
    response.cookies.set("splice_token", "", { maxAge: 0, path: "/" });
    response.cookies.set("splice_refresh_token", "", { maxAge: 0, path: "/" });
    response.cookies.set("splice_auth_status", "", { maxAge: 0, path: "/" });
    response.cookies.set("splice_user", "", { maxAge: 0, path: "/" });

    return response;
  }
}

