import { NextRequest, NextResponse } from "next/server";
import { csrfGuard } from "@/lib/csrf-server";

// Force Node.js runtime for better compatibility
export const runtime = "nodejs";
export const maxDuration = 30;

// =============================================================================
// IP-based Rate Limiting
// =============================================================================

// Rate limit configuration: 5 requests per hour per IP
const RATE_LIMIT_WINDOW_MS = 60 * 60 * 1000; // 1 hour in milliseconds
const RATE_LIMIT_MAX_REQUESTS = 5;

// In-memory store for rate limiting
// Key: IP address, Value: { count: number, windowStart: number }
const rateLimitStore = new Map<string, { count: number; windowStart: number }>();

// Cleanup old entries every 10 minutes to prevent memory leaks
if (typeof setInterval !== "undefined") {
  setInterval(() => {
    const now = Date.now();
    const ipsToDelete: string[] = [];
    rateLimitStore.forEach((data, ip) => {
      // Mark entries older than 2x the window (2 hours) for deletion
      if (now - data.windowStart > RATE_LIMIT_WINDOW_MS * 2) {
        ipsToDelete.push(ip);
      }
    });
    ipsToDelete.forEach((ip) => rateLimitStore.delete(ip));
  }, 10 * 60 * 1000); // Every 10 minutes
}

/**
 * Get client IP address from request headers
 * Handles various proxy configurations (Vercel, Cloudflare, nginx, etc.)
 */
function getClientIp(request: NextRequest): string {
  // x-forwarded-for can contain multiple IPs; the first one is the client
  const forwardedFor = request.headers.get("x-forwarded-for");
  if (forwardedFor) {
    const firstIp = forwardedFor.split(",")[0].trim();
    if (firstIp) return firstIp;
  }

  // x-real-ip is set by some proxies (nginx, etc.)
  const realIp = request.headers.get("x-real-ip");
  if (realIp) return realIp.trim();

  // Vercel-specific header
  const vercelForwardedFor = request.headers.get("x-vercel-forwarded-for");
  if (vercelForwardedFor) {
    const firstIp = vercelForwardedFor.split(",")[0].trim();
    if (firstIp) return firstIp;
  }

  // Fallback - this shouldn't happen in production behind a proxy
  return "unknown";
}

/**
 * Check if an IP is rate limited
 * Returns: { limited: boolean, retryAfter?: number }
 */
function checkRateLimit(ip: string): { limited: boolean; retryAfter?: number } {
  const now = Date.now();
  const data = rateLimitStore.get(ip);

  // No previous requests or window expired - start fresh
  if (!data || now - data.windowStart > RATE_LIMIT_WINDOW_MS) {
    rateLimitStore.set(ip, { count: 1, windowStart: now });
    return { limited: false };
  }

  // Increment count
  data.count++;

  // Check if over limit
  if (data.count > RATE_LIMIT_MAX_REQUESTS) {
    // Calculate seconds until window resets
    const retryAfterMs = RATE_LIMIT_WINDOW_MS - (now - data.windowStart);
    const retryAfterSeconds = Math.ceil(retryAfterMs / 1000);
    return { limited: true, retryAfter: retryAfterSeconds };
  }

  return { limited: false };
}

// Email configuration
const EMAIL_CONFIG = {
  from: process.env.EMAIL_FROM || "SPLICE <noreply@spliceclips.com>",
  salesEmail: "sales@spliceclips.com",
  sendgridApiKey: process.env.SENDGRID_API_KEY,
};

// Request body interface
interface ContactFormData {
  name: string;
  email: string;
  company?: string;
  teamSize?: string;
  message: string;
}

// Validation helper
function validateEmail(email: string): boolean {
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  return emailRegex.test(email);
}

// Escape HTML to prevent XSS in email content
function escapeHtml(str: string): string {
  if (str === null || str === undefined) return "";
  const escapeMap: Record<string, string> = {
    "&": "&amp;",
    "<": "&lt;",
    ">": "&gt;",
    '"': "&quot;",
    "'": "&#x27;",
  };
  return str.replace(/[&<>"']/g, (char) => escapeMap[char]);
}

// Generate email HTML template
function generateEmailHtml(data: ContactFormData): string {
  return `
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>New Contact Form Submission</title>
  <style>
    body { font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif; line-height: 1.6; color: #333; background: #f5f5f5; margin: 0; padding: 0; }
    .container { max-width: 600px; margin: 0 auto; padding: 40px 20px; background: white; }
    .header { text-align: center; margin-bottom: 30px; padding-bottom: 20px; border-bottom: 2px solid #00D4FF; }
    .logo { font-size: 28px; font-weight: bold; color: #00D4FF; }
    .badge { display: inline-block; background: #00D4FF; color: white; padding: 4px 12px; border-radius: 20px; font-size: 12px; margin-top: 10px; }
    .content { padding: 20px 0; }
    .field { margin-bottom: 20px; }
    .field-label { font-size: 12px; text-transform: uppercase; letter-spacing: 1px; color: #6b7280; margin-bottom: 5px; }
    .field-value { font-size: 16px; color: #1e293b; background: #f8fafc; padding: 12px 16px; border-radius: 8px; border-left: 3px solid #00D4FF; }
    .message-box { background: #f0fdf4; border: 1px solid #86efac; border-radius: 8px; padding: 20px; margin-top: 20px; }
    .message-box h3 { color: #166534; margin-top: 0; font-size: 14px; text-transform: uppercase; letter-spacing: 1px; }
    .message-content { color: #15803d; white-space: pre-wrap; }
    .footer { text-align: center; margin-top: 40px; padding-top: 20px; border-top: 1px solid #e5e7eb; color: #6b7280; font-size: 14px; }
    .reply-button { display: inline-block; background: #00D4FF; color: white; padding: 12px 24px; border-radius: 6px; text-decoration: none; margin-top: 20px; font-weight: 500; }
  </style>
</head>
<body>
  <div class="container">
    <div class="header">
      <div class="logo">SPLICE</div>
      <div class="badge">New Contact Form Submission</div>
    </div>

    <div class="content">
      <div class="field">
        <div class="field-label">Name</div>
        <div class="field-value">${escapeHtml(data.name)}</div>
      </div>

      <div class="field">
        <div class="field-label">Email</div>
        <div class="field-value"><a href="mailto:${escapeHtml(data.email)}" style="color: #00A8CC;">${escapeHtml(data.email)}</a></div>
      </div>

      ${
        data.company
          ? `
      <div class="field">
        <div class="field-label">Company</div>
        <div class="field-value">${escapeHtml(data.company)}</div>
      </div>
      `
          : ""
      }

      ${
        data.teamSize
          ? `
      <div class="field">
        <div class="field-label">Team Size</div>
        <div class="field-value">${escapeHtml(data.teamSize)}</div>
      </div>
      `
          : ""
      }

      <div class="message-box">
        <h3>Message</h3>
        <div class="message-content">${escapeHtml(data.message)}</div>
      </div>

      <div style="text-align: center;">
        <a href="mailto:${escapeHtml(data.email)}?subject=Re: SPLICE Contact Form" class="reply-button">Reply to ${escapeHtml(data.name)}</a>
      </div>
    </div>

    <div class="footer">
      <p>This message was sent from the SPLICE website contact form.</p>
      <p style="font-size: 12px;">&copy; ${new Date().getFullYear()} SPLICE. All rights reserved.</p>
    </div>
  </div>
</body>
</html>
  `;
}

// Generate plain text email
function generateEmailText(data: ContactFormData): string {
  return `
New Contact Form Submission - SPLICE
=====================================

Name: ${data.name}
Email: ${data.email}
${data.company ? `Company: ${data.company}` : ""}
${data.teamSize ? `Team Size: ${data.teamSize}` : ""}

Message:
---------
${data.message}

---
This message was sent from the SPLICE website contact form.
  `.trim();
}

// Send email via SendGrid
async function sendEmailNotification(data: ContactFormData): Promise<void> {
  if (!EMAIL_CONFIG.sendgridApiKey) {
    console.warn("[Contact API] SENDGRID_API_KEY not configured, logging email to console");
    console.log("[Contact API] Would send email to:", EMAIL_CONFIG.salesEmail);
    console.log("[Contact API] From:", data.name, `<${data.email}>`);
    console.log("[Contact API] Message:", data.message);
    return;
  }

  const response = await fetch("https://api.sendgrid.com/v3/mail/send", {
    method: "POST",
    headers: {
      Authorization: `Bearer ${EMAIL_CONFIG.sendgridApiKey}`,
      "Content-Type": "application/json",
    },
    body: JSON.stringify({
      personalizations: [
        {
          to: [{ email: EMAIL_CONFIG.salesEmail }],
        },
      ],
      from: {
        email: EMAIL_CONFIG.from.match(/<(.+)>/)?.[1] || "noreply@spliceclips.com",
        name: "SPLICE Contact Form",
      },
      reply_to: {
        email: data.email,
        name: data.name,
      },
      subject: `[SPLICE Contact] New inquiry from ${data.name}${data.company ? ` (${data.company})` : ""}`,
      content: [
        { type: "text/plain", value: generateEmailText(data) },
        { type: "text/html", value: generateEmailHtml(data) },
      ],
    }),
  });

  if (!response.ok) {
    const errorText = await response.text();
    console.error("[Contact API] SendGrid error:", response.status, errorText);
    throw new Error(`Failed to send email: ${response.status}`);
  }

  console.log("[Contact API] Email sent successfully to", EMAIL_CONFIG.salesEmail);
}

export async function POST(request: NextRequest) {
  // CSRF validation first
  const csrfError = await csrfGuard(request);
  if (csrfError) {
    console.warn("[Contact API] CSRF validation failed");
    return csrfError;
  }

  // Check rate limit
  const clientIp = getClientIp(request);
  const rateLimitResult = checkRateLimit(clientIp);

  if (rateLimitResult.limited) {
    console.warn(
      `[Contact API] Rate limit exceeded for IP: ${clientIp}`
    );

    const retryAfter = rateLimitResult.retryAfter || 3600;
    const retryMinutes = Math.ceil(retryAfter / 60);

    return NextResponse.json(
      {
        error: "Too many requests",
        message: `You have exceeded the maximum number of contact form submissions. Please try again in ${retryMinutes} minute${retryMinutes === 1 ? "" : "s"}.`,
        retryAfter: retryAfter,
      },
      {
        status: 429,
        headers: {
          "Retry-After": String(retryAfter),
        },
      }
    );
  }

  try {
    // Parse request body
    let body: ContactFormData;
    try {
      body = await request.json();
    } catch {
      return NextResponse.json(
        { error: "Invalid JSON in request body" },
        { status: 400 }
      );
    }

    const { name, email, company, teamSize, message } = body;

    // Validate required fields
    const errors: string[] = [];

    if (!name || typeof name !== "string" || name.trim().length === 0) {
      errors.push("Name is required");
    }

    if (!email || typeof email !== "string" || email.trim().length === 0) {
      errors.push("Email is required");
    } else if (!validateEmail(email.trim())) {
      errors.push("Invalid email format");
    }

    if (!message || typeof message !== "string" || message.trim().length === 0) {
      errors.push("Message is required");
    }

    if (errors.length > 0) {
      return NextResponse.json(
        { error: "Validation failed", details: errors },
        { status: 400 }
      );
    }

    // Sanitize and prepare data
    const sanitizedData: ContactFormData = {
      name: name.trim(),
      email: email.trim().toLowerCase(),
      company: company?.trim() || undefined,
      teamSize: teamSize?.trim() || undefined,
      message: message.trim(),
    };

    // Send email notification
    await sendEmailNotification(sanitizedData);

    // Return success response
    return NextResponse.json({
      success: true,
      message: "Your message has been sent successfully. We will get back to you soon!",
    });
  } catch (error) {
    console.error("[Contact API] Error processing contact form:", error);

    return NextResponse.json(
      {
        error: "Failed to process your request. Please try again later.",
        details: error instanceof Error ? error.message : "Unknown error",
      },
      { status: 500 }
    );
  }
}

