/**
 * Shared Redis Client Service
 * Supports both Upstash REST API and standard Redis (ioredis)
 * - Uses @upstash/redis when UPSTASH_REDIS_REST_URL is configured
 * - Uses ioredis when UPSTASH_REDIS_URL is configured (Railway, standard Redis)
 * - Provides unified API for caching and storage operations
 */

const { Redis: UpstashRedis } = require('@upstash/redis');
const IORedis = require('ioredis');

// Singleton instances
let client = null;
let clientType = 'none'; // 'upstash-rest', 'ioredis', or 'none'
let isConnected = false;
let connectionError = null;

/**
 * Initialize Redis client based on available environment variables
 * Priority: Upstash REST > Standard Redis URL
 * @returns {Object|null} Redis client or null if not configured
 */
function initializeClient() {
  if (client) {
    return client;
  }

  // Try Upstash REST first (for Upstash-specific deployments)
  const restUrl = process.env.UPSTASH_REDIS_REST_URL;
  const restToken = process.env.UPSTASH_REDIS_REST_TOKEN;

  if (restUrl && restToken) {
    try {
      client = new UpstashRedis({
        url: restUrl,
        token: restToken,
        automaticDeserialization: true,
      });
      clientType = 'upstash-rest';
      isConnected = true;
      console.log('[Redis] Upstash REST client initialized');
      return client;
    } catch (err) {
      console.error('[Redis] Failed to initialize Upstash REST client:', err.message);
      connectionError = err;
    }
  }

  // Try standard Redis URL (Railway, Docker, local Redis)
  const redisUrl = process.env.UPSTASH_REDIS_URL || process.env.REDIS_URL;

  if (redisUrl) {
    try {
      client = new IORedis(redisUrl, {
        maxRetriesPerRequest: 3,
        retryDelayOnFailover: 100,
        lazyConnect: false,
        enableReadyCheck: true,
        connectTimeout: 10000,
      });

      client.on('connect', () => {
        isConnected = true;
        connectionError = null;
        console.log('[Redis] ioredis client connected');
      });

      client.on('error', (err) => {
        console.error('[Redis] ioredis error:', err.message);
        connectionError = err;
        // Don't set isConnected to false on transient errors
      });

      client.on('close', () => {
        console.log('[Redis] ioredis connection closed');
      });

      clientType = 'ioredis';
      isConnected = true; // Optimistically set, will be updated by events
      console.log('[Redis] ioredis client initialized with URL');
      return client;
    } catch (err) {
      console.error('[Redis] Failed to initialize ioredis client:', err.message);
      connectionError = err;
    }
  }

  console.warn('[Redis] No Redis configuration found - Redis features disabled');
  console.warn('[Redis] Set UPSTASH_REDIS_URL for standard Redis or UPSTASH_REDIS_REST_URL + UPSTASH_REDIS_REST_TOKEN for Upstash REST');
  connectionError = new Error('Redis not configured');
  return null;
}

/**
 * Get the Redis client (lazy initialization)
 * @returns {Object|null} Redis client or null if not configured
 */
function getRedisClient() {
  return initializeClient();
}

/**
 * Check if Redis is available and connected
 * @returns {boolean} True if Redis is available
 */
function isRedisAvailable() {
  const redis = getRedisClient();
  return redis !== null && isConnected;
}

/**
 * Get Redis connection status
 * @returns {Object} { connected, error, type }
 */
function getRedisStatus() {
  getRedisClient(); // Ensure initialization attempted
  return {
    connected: isConnected && client !== null,
    error: connectionError ? connectionError.message : null,
    type: clientType
  };
}

/**
 * Ping Redis to check connectivity
 * @returns {Promise<boolean>} True if ping successful
 */
async function pingRedis() {
  const redis = getRedisClient();
  if (!redis) {
    return false;
  }

  try {
    const result = await redis.ping();
    const success = result === 'PONG' || result === 'pong';
    if (success) {
      isConnected = true;
      connectionError = null;
    }
    return success;
  } catch (err) {
    console.error('[Redis] Ping failed:', err.message);
    isConnected = false;
    connectionError = err;
    return false;
  }
}

/**
 * Set a value with optional TTL
 * @param {string} key - Redis key
 * @param {any} value - Value to store (will be JSON serialized for ioredis)
 * @param {number} ttlSeconds - Optional TTL in seconds
 * @returns {Promise<boolean>} True if successful
 */
async function set(key, value, ttlSeconds = null) {
  const redis = getRedisClient();
  if (!redis) {
    return false;
  }

  try {
    // Serialize value for ioredis (Upstash REST handles this automatically)
    const serializedValue = clientType === 'ioredis' ? JSON.stringify(value) : value;

    if (clientType === 'upstash-rest') {
      if (ttlSeconds) {
        await redis.set(key, value, { ex: ttlSeconds });
      } else {
        await redis.set(key, value);
      }
    } else {
      // ioredis syntax
      if (ttlSeconds) {
        await redis.set(key, serializedValue, 'EX', ttlSeconds);
      } else {
        await redis.set(key, serializedValue);
      }
    }
    return true;
  } catch (err) {
    console.error('[Redis] SET failed:', err.message);
    return false;
  }
}

/**
 * Get a value
 * @param {string} key - Redis key
 * @returns {Promise<any|null>} Value or null if not found
 */
async function get(key) {
  const redis = getRedisClient();
  if (!redis) {
    return null;
  }

  try {
    const result = await redis.get(key);
    if (result === null) {
      return null;
    }
    // Parse JSON for ioredis (Upstash REST handles this automatically)
    if (clientType === 'ioredis' && typeof result === 'string') {
      try {
        return JSON.parse(result);
      } catch {
        return result; // Return as-is if not JSON
      }
    }
    return result;
  } catch (err) {
    console.error('[Redis] GET failed:', err.message);
    return null;
  }
}

/**
 * Check if a key exists
 * @param {string} key - Redis key
 * @returns {Promise<boolean>} True if key exists
 */
async function exists(key) {
  const redis = getRedisClient();
  if (!redis) {
    return false;
  }

  try {
    const result = await redis.exists(key);
    return result === 1 || result === true;
  } catch (err) {
    console.error('[Redis] EXISTS failed:', err.message);
    return false;
  }
}

/**
 * Delete a key
 * @param {string} key - Redis key
 * @returns {Promise<boolean>} True if deleted
 */
async function del(key) {
  const redis = getRedisClient();
  if (!redis) {
    return false;
  }

  try {
    await redis.del(key);
    return true;
  } catch (err) {
    console.error('[Redis] DEL failed:', err.message);
    return false;
  }
}

/**
 * Set a value only if it doesn't exist (SETNX)
 * @param {string} key - Redis key
 * @param {any} value - Value to store
 * @param {number} ttlSeconds - Optional TTL in seconds
 * @returns {Promise<boolean>} True if set (key didn't exist)
 */
async function setNX(key, value, ttlSeconds = null) {
  const redis = getRedisClient();
  if (!redis) {
    return false;
  }

  try {
    const serializedValue = clientType === 'ioredis' ? JSON.stringify(value) : value;

    if (clientType === 'upstash-rest') {
      const options = { nx: true };
      if (ttlSeconds) {
        options.ex = ttlSeconds;
      }
      const result = await redis.set(key, value, options);
      return result === 'OK';
    } else {
      // ioredis: Use SET with NX option
      const args = [key, serializedValue, 'NX'];
      if (ttlSeconds) {
        args.push('EX', ttlSeconds);
      }
      const result = await redis.set(...args);
      return result === 'OK';
    }
  } catch (err) {
    console.error('[Redis] SETNX failed:', err.message);
    return false;
  }
}

/**
 * Get TTL of a key
 * @param {string} key - Redis key
 * @returns {Promise<number>} TTL in seconds, -1 if no TTL, -2 if key doesn't exist
 */
async function ttl(key) {
  const redis = getRedisClient();
  if (!redis) {
    return -2;
  }

  try {
    return await redis.ttl(key);
  } catch (err) {
    console.error('[Redis] TTL failed:', err.message);
    return -2;
  }
}

/**
 * Gracefully close the Redis connection
 * @returns {Promise<void>}
 */
async function close() {
  if (client && clientType === 'ioredis') {
    try {
      await client.quit();
      console.log('[Redis] ioredis connection closed gracefully');
    } catch (err) {
      console.error('[Redis] Error closing connection:', err.message);
    }
  }
  client = null;
  clientType = 'none';
  isConnected = false;
}

module.exports = {
  getRedisClient,
  isRedisAvailable,
  getRedisStatus,
  pingRedis,
  close,
  // Convenience methods
  set,
  get,
  exists,
  del,
  setNX,
  ttl
};

/**
 * Usage Tracking Service
 *
 * Tracks user credit balances and usage for billing.
 * Uses PostgreSQL for persistence.
 */

const { Pool } = require('pg');
const { reportHoursOverage, reportMusicOverage } = require('./stripeUsageReporting');

// PostgreSQL connection pool with proper limits
// SSL Configuration:
// - Railway managed PostgreSQL uses internal certificates, requires rejectUnauthorized: false
// - For other providers, set DATABASE_SSL_CA env var with CA certificate content
// - Set DATABASE_SSL_REJECT_UNAUTHORIZED=true to enable strict SSL validation
const sslConfig = process.env.NODE_ENV === 'production' ? {
  rejectUnauthorized: process.env.DATABASE_SSL_REJECT_UNAUTHORIZED === 'true',
  ca: process.env.DATABASE_SSL_CA || undefined
} : false;

const pool = new Pool({
  connectionString: process.env.DATABASE_URL,
  ssl: sslConfig,
  // PERFORMANCE: Set connection pool limits
  max: 20,                    // Maximum number of clients in the pool
  idleTimeoutMillis: 30000,   // Close idle connections after 30 seconds
  // MEDIUM-007: Database Query Timeouts - prevent long-running queries from exhausting pool
  connectionTimeoutMillis: 10000,  // 10 seconds to acquire connection (increased for reliability)
  statement_timeout: 30000,        // 30 seconds max query execution time
  query_timeout: 30000             // 30 seconds max total query time including queue
});

// Hours per tier (regular processing)
// Pricing: Starter $15/mo, Pro $39/mo, Team $129/mo
// Target margins: 79-82%
const TIER_HOURS = {
  starter: 4,    // 4 hrs/month
  pro: 15,       // 15 hrs/month
  team: 50,      // 50 hrs/month
  cancelled: 0
};

// Overage rate for processing hours beyond included
const HOURS_OVERAGE_RATE = 2.00; // $2/hour

// Isolation minutes per tier (sized to maintain 80% margins)
const TIER_ISOLATION_MINUTES = {
  starter: 0,     // No isolation access
  pro: 45,        // 45 minutes included
  team: 180,      // 3 hours included
  cancelled: 0
};

// Convert to hours for database storage
const TIER_ISOLATION_HOURS = {
  starter: 0,
  pro: TIER_ISOLATION_MINUTES.pro / 60,      // 0.75 hours
  team: TIER_ISOLATION_MINUTES.team / 60,    // 3 hours
  cancelled: 0
};

// Overage rate for isolation beyond included minutes
const ISOLATION_OVERAGE_RATE = 0.10; // $0.10 per minute (85% margin)

/**
 * Initialize database tables
 */
async function initDatabase() {
  const client = await pool.connect();
  try {
    await client.query(`
      CREATE TABLE IF NOT EXISTS users (
        id SERIAL PRIMARY KEY,
        stripe_customer_id VARCHAR(255) UNIQUE NOT NULL,
        email VARCHAR(255),
        tier VARCHAR(50) DEFAULT 'starter',
        hours_remaining DECIMAL(10,4) DEFAULT 15,
        hours_total DECIMAL(10,4) DEFAULT 15,
        isolation_hours_remaining DECIMAL(10,4) DEFAULT 0,
        isolation_hours_total DECIMAL(10,4) DEFAULT 0,
        created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
        updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
      )
    `);

    // Add isolation columns if they don't exist (for existing databases)
