  return true;
}

// ============================================================================
// EXPORTS
// ============================================================================

module.exports = {
  sendEmail,
  sendLicenseKeyEmail,
  sendPaymentWarningEmail,
  sendLicenseLookupEmail,
  sendSignInCodeEmail,
  sendEmailVerificationCode,
  isEmailConfigured,
  EMAIL_CONFIG,
  // CRITICAL-002: Exported email validation helpers
  validateAndNormalizeEmail,
  extractAndValidateEmail
};

