   497→   497→/**
   498→   498→ * Clear all auth tokens (call on logout or auth failure)
   499→   499→ */
   500→   500→function clearAuthTokens() {
   501→   501→    saveSettings({
   502→   502→        accessToken: null,
   503→   503→        refreshToken: null,
   504→   504→        tokenExpiry: null
   505→   505→    });
   506→   506→    debugLog('[Auth] Auth tokens cleared');
   507→   507→}
