   509→   509→/**
   510→   510→ * Ensure we have a valid access token, refreshing if needed
   511→   511→ * @returns {Promise<boolean>} True if we have a valid token
   512→   512→ */
   513→   513→async function ensureValidToken() {
   514→   514→    const settings = getSettings();
   515→   515→
   516→   516→    // No token at all - need to login
   517→   517→    if (!settings.accessToken) {
   518→   518→        return false;
   519→   519→    }
   520→   520→
   521→   521→    // Token not expired - good to go
   522→   522→    if (!isTokenExpired()) {
   523→   523→        return true;
   524→   524→    }
   525→   525→
   526→   526→    // Try to refresh
   527→   527→    return await refreshAccessToken();
   528→   528→}
