   811→   811→function decryptFromStorage(data) {
   812→   812→    // Handle new encrypted format by returning null (needs async)
   813→   813→    if (data && data.startsWith(SPLICE_ENCRYPTION.PREFIX)) {
   814→   814→        debugLog('[Config] Encrypted data requires async decryption');
   815→   815→        return null;
   816→   816→    }
   817→   817→    return legacyDecode(data);
