   819→   819→
   820→   820→/**
   821→   821→ * Check if a value appears to be encrypted
   822→   822→ *
   823→   823→ * @param {any} value - Value to check
   824→   824→ * @returns {boolean} True if value appears to be encrypted
   825→   825→ */
   826→   826→function isEncryptedValue(value) {
   827→   827→    if (!value || typeof value !== 'string') {
   828→   828→        return false;
   829→   829→    }
   830→   830→    // New encryption format
   831→   831→    if (value.startsWith(SPLICE_ENCRYPTION.PREFIX)) {
   832→   832→        return true;
   833→   833→    }
   834→   834→    // Unencrypted values typically start with known patterns
   835→   835→    if (value.startsWith('cus_') || value.startsWith('SPLICE-')) {
   836→   836→        return false;
   837→   837→    }
   838→   838→    // Check if it's legacy base64 encoded JSON
   839→   839→    try {
   840→   840→        const decoded = atob(value);
   841→   841→        const jsonStr = decodeURIComponent(decoded);
   842→   842→        JSON.parse(jsonStr);
   843→   843→        return true;
   844→   844→    } catch {
   845→   845→        return false;
   846→   846→    }
   847→   847→}
