   849→   849→/**
   850→   850→ * Encrypt sensitive fields in settings object
   851→   851→ *
   852→   852→ * @param {Object} settings - Settings object with plaintext sensitive fields
   853→   853→ * @returns {Object} Settings with encrypted sensitive fields
   854→   854→ */
   855→   855→function encryptSensitiveFields(settings) {
   856→   856→    const encrypted = { ...settings };
   857→   857→
   858→   858→    for (const field of SENSITIVE_FIELDS) {
   859→   859→        if (encrypted[field] !== null && encrypted[field] !== undefined) {
   860→   860→            // Only encrypt if not already encrypted
   861→   861→            if (!isEncryptedValue(encrypted[field])) {
   862→   862→                encrypted[field] = encryptForStorage(encrypted[field]);
   863→   863→                debugLog(`[Config] Encrypted field: ${field}`);
   864→   864→            }
   865→   865→        }
   866→   866→    }
   867→   867→
   868→   868→    return encrypted;
   869→   869→}
