   874→   874→ * @param {Object} settings - Settings object with encrypted sensitive fields
   875→   875→ * @returns {Object} Settings with decrypted sensitive fields
   876→   876→ */
   877→   877→function decryptSensitiveFields(settings) {
   878→   878→    const decrypted = { ...settings };
   879→   879→
   880→   880→    for (const field of SENSITIVE_FIELDS) {
   881→   881→        if (decrypted[field] !== null && decrypted[field] !== undefined) {
   882→   882→            // Check if it looks encrypted before attempting to decrypt
   883→   883→            if (isEncryptedValue(decrypted[field])) {
   884→   884→                const decryptedValue = decryptFromStorage(decrypted[field]);
   885→   885→                if (decryptedValue !== null) {
   886→   886→                    decrypted[field] = decryptedValue;
   887→   887→                    debugLog(`[Config] Decrypted field: ${field}`);
   888→   888→                }
   889→   889→            }
   890→   890→            // If not encrypted (legacy data), keep the original value
   891→   891→        }
   892→   892→    }
   893→   893→
   894→   894→    return decrypted;
   895→   895→}
