   897→   897→/**
   898→   898→ * Internal save function to avoid recursion during migration
   899→   899→ * @private
   900→   900→ */
   901→   901→function _saveSettingsInternal(settings) {
   902→   902→    try {
   903→   903→        // Encrypt sensitive fields before storing
   904→   904→        const toStore = encryptSensitiveFields(settings);
   905→   905→
   906→   906→        // Add version marker for future migrations
   907→   907→        toStore._storageVersion = SETTINGS_STORAGE_VERSION;
   908→   908→
   909→   909→        localStorage.setItem('spliceSettings', JSON.stringify(toStore));
   910→   910→        return true;
   911→   911→    } catch (e) {
   912→   912→        console.warn('[Config] Error saving settings:', e);
   913→   913→        return false;
