   917→   917→/**
   918→   918→ * Get settings from localStorage
   919→   919→ * Handles migration from unencrypted to encrypted storage format
   920→   920→ */
   921→   921→function getSettings() {
   922→   922→    try {
   923→   923→        const stored = localStorage.getItem('spliceSettings');
   924→   924→        if (stored) {
   925→   925→            const parsed = JSON.parse(stored);
   926→   926→
   927→   927→            // Decrypt sensitive fields (handles both encrypted and legacy unencrypted data)
   928→   928→            const decrypted = decryptSensitiveFields(parsed);
   929→   929→
   930→   930→            // Check if migration is needed (no version marker = legacy data)
   931→   931→            if (!parsed._storageVersion) {
   932→   932→                // Migrate legacy data: save with encryption and version marker
   933→   933→                debugLog('[Config] Migrating legacy settings to encrypted format');
   934→   934→                const migratedSettings = { ...DEFAULT_SETTINGS, ...decrypted };
   935→   935→                // Use internal save to avoid infinite recursion
   936→   936→                _saveSettingsInternal(migratedSettings);
   937→   937→            }
   938→   938→
   939→   939→            // Return merged settings without internal fields
   940→   940→            const result = { ...DEFAULT_SETTINGS, ...decrypted };
   941→   941→            delete result._storageVersion;
   942→   942→            return result;
   943→   943→        }
   944→   944→    } catch (e) {
   945→   945→        console.warn('[Config] Error loading settings:', e);
   946→   946→    }
   947→   947→    return { ...DEFAULT_SETTINGS };
   948→   948→}
