  1085→  1085→/**
  1086→  1086→ * Format time in MM:SS or HH:MM:SS
  1087→  1087→ */
  1088→  1088→function formatTime(seconds) {
  1089→  1089→    if (isNaN(seconds) || seconds < 0) return '0:00';
  1090→  1090→
  1091→  1091→    const hours = Math.floor(seconds / 3600);
  1092→  1092→    const minutes = Math.floor((seconds % 3600) / 60);
  1093→  1093→    const secs = Math.floor(seconds % 60);
  1094→  1094→
  1095→  1095→    if (hours > 0) {
  1096→  1096→        return `${hours}:${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  1097→  1097→    }
  1098→  1098→    return `${minutes}:${secs.toString().padStart(2, '0')}`;
  1099→  1099→}
