  1112→  1112→/**
  1113→  1113→ * Debounce function calls
  1114→  1114→ */
  1115→  1115→function debounce(func, wait) {
  1116→  1116→    let timeout;
  1117→  1117→    return function executedFunction(...args) {
  1118→  1118→        const later = () => {
  1119→  1119→            clearTimeout(timeout);
  1120→  1120→            func(...args);
  1121→  1121→        };
  1122→  1122→        clearTimeout(timeout);
  1123→  1123→        timeout = setTimeout(later, wait);
  1124→  1124→    };
  1125→  1125→}
