  1372→  1372→/**
  1373→  1373→ * Validate license key format: SPLICE-XXXX-XXXX-XXXX
  1374→  1374→ * Character set excludes confusing chars: 0/O, 1/I/L
  1375→  1375→ */
  1376→  1376→function isValidLicenseKeyFormat(key) {
  1377→  1377→    if (!key || typeof key !== 'string') return false;
  1378→  1378→    const normalized = key.toUpperCase().trim();
  1379→  1379→    const pattern = /^SPLICE-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}$/;
  1380→  1380→    return pattern.test(normalized);
  1381→  1381→}
