  1486→  1486→                debugLog('Error constructor:', err?.constructor?.name);
  1487→  1487→                debugLog('Is TypeError:', err?.name === 'TypeError');
  1488→  1488→                debugLog('Message contains "Failed to fetch":', err?.message?.includes('Failed to fetch'));
  1489→  1489→                debugLog('Message contains "fetch":', err?.message?.toLowerCase()?.includes('fetch'));
  1490→  1490→                debugLog('Message contains "network":', err?.message?.toLowerCase()?.includes('network'));
  1491→  1491→                debugLog('Message contains "CORS":', err?.message?.toLowerCase()?.includes('cors'));
  1492→  1492→                debugLog('Message contains "SSL":', err?.message?.toLowerCase()?.includes('ssl'));
  1493→  1493→                debugLog('Message contains "certificate":', err?.message?.toLowerCase()?.includes('certificate'));
  1494→  1494→                debugLog('Full error object:', JSON.stringify(err, Object.getOwnPropertyNames(err)));
  1495→  1495→                debugLog('================================================');
  1496→  1496→
  1497→  1497→                // Provide detailed error message based on error type
  1498→  1498→                let errorMessage = 'Connection error. ';
  1499→  1499→
  1500→  1500→                if (err.message === 'Request timed out') {
  1501→  1501→                    errorMessage += 'Server is not responding. Check if backend is running.';
  1502→  1502→                } else if (err.message?.includes('Failed to fetch') || err.name === 'TypeError') {
