  1698→  1698→// ============================================================================
  1699→  1699→// CENTRALIZED ERROR LOGGING (FIX: CEP-ERR-007)
  1700→  1700→// Captures errors for debugging across different Mac configurations
  1701→  1701→// ============================================================================
  1702→  1702→
  1703→  1703→/**
  1704→  1704→ * Error log storage - persisted to localStorage for debugging
  1705→  1705→ * Stores the last 50 errors with timestamps and context
  1706→  1706→ */
  1707→  1707→const ERROR_LOG_KEY = 'spliceErrorLog';
  1708→  1708→const MAX_ERROR_LOG_SIZE = 50;
  1709→  1709→
  1710→  1710→/**
  1711→  1711→ * Log an error with context for debugging
  1712→  1712→ * FIX: CEP-ERR-007 - Centralized error capture for debugging crashes
  1713→  1713→ *
  1714→  1714→ * @param {string} module - Module name (e.g., 'JSX', 'Silence', 'Takes')
  1715→  1715→ * @param {string} operation - Operation that failed
  1716→  1716→ * @param {Error|string} error - Error object or message
  1717→  1717→ * @param {Object} context - Additional context for debugging
  1718→  1718→ */
  1719→  1719→function logError(module, operation, error, context = {}) {
  1720→  1720→    const errorEntry = {
  1721→  1721→        timestamp: new Date().toISOString(),
  1722→  1722→        module,
  1723→  1723→        operation,
  1724→  1724→        message: error?.message || String(error),
  1725→  1725→        stack: error?.stack || null,
  1726→  1726→        code: error?.code || null,
  1727→  1727→        context: {
  1728→  1728→            ...context,
  1729→  1729→            userAgent: navigator?.userAgent,
  1730→  1730→            isCEP: typeof window.__adobe_cep__ !== 'undefined',
  1731→  1731→            isOnline: navigator?.onLine
  1732→  1732→        }
  1733→  1733→    };
  1734→  1734→
  1735→  1735→    // Log to console
  1736→  1736→    console.error(`[SPLICE ${module}] ${operation}:`, error);
  1737→  1737→    if (SPLICE_DEBUG) {
  1738→  1738→        console.error('[SPLICE ERROR CONTEXT]', errorEntry);
  1739→  1739→    }
  1740→  1740→
  1741→  1741→    // Persist to localStorage for later debugging
  1742→  1742→    try {
  1743→  1743→        let errorLog = [];
  1744→  1744→        const stored = localStorage.getItem(ERROR_LOG_KEY);
  1745→  1745→        if (stored) {
  1746→  1746→            errorLog = JSON.parse(stored);
  1747→  1747→        }
  1748→  1748→
  1749→  1749→        // Add new error and trim to max size
  1750→  1750→        errorLog.unshift(errorEntry);
  1751→  1751→        if (errorLog.length > MAX_ERROR_LOG_SIZE) {
  1752→  1752→            errorLog = errorLog.slice(0, MAX_ERROR_LOG_SIZE);
  1753→  1753→        }
  1754→  1754→
  1755→  1755→        localStorage.setItem(ERROR_LOG_KEY, JSON.stringify(errorLog));
  1756→  1756→    } catch (e) {
