function isProduction() {
  return process.env.NODE_ENV === 'production' || !!process.env.RAILWAY_ENVIRONMENT;
}

/**
 * SECURITY: Sanitize error message for production
 * Hides internal error details from clients in production
 *
 * @param {Error} err - Error object
 * @param {string} fallbackMessage - Message to show in production
 * @returns {string} Sanitized error message
 */
function sanitizeError(err, fallbackMessage = 'An error occurred') {
  // In production, only show the fallback message
  // Exception: If error is an AppError (operational), show its message
  if (isProduction()) {
    if (err instanceof AppError && err.isOperational) {
      return err.message;
    }
    return fallbackMessage;
  }

  // In development, show full error message
  return err.message || fallbackMessage;
}

/**
 * SECURITY: Create sanitized error response
 * Use this instead of directly using err.message in responses
 *
 * @param {Error} err - Error object
 * @param {string} fallbackMessage - Message to show in production
 * @returns {Object} Sanitized error response object
 */
function createErrorResponse(err, fallbackMessage = 'An error occurred') {
  return { error: sanitizeError(err, fallbackMessage) };
}

/**
 * SECURITY: Log error internally and return sanitized response
 * Convenience function for catch blocks in route handlers
 *
 * This function:
 * 1. Logs the full error details internally (with stack trace)
 * 2. Returns a sanitized error message safe for client response
 *
 * @param {Error} err - Error object
 * @param {string} context - Context string for logging (e.g., '[SPLICE] Music generation')
 * @param {string} fallbackMessage - Message to show in production
 * @returns {string} Sanitized error message for client
 *
 * @example
 * catch (err) {
 *   const message = logAndSanitize(err, '[SPLICE] Music generation', 'Failed to generate music');
 *   res.status(500).json({ error: message });
 * }
 */
function logAndSanitize(err, context = '[SPLICE]', fallbackMessage = 'An error occurred') {
  // Always log full error internally
  console.error(`${context} Error:`, {
    message: err.message,
    stack: err.stack,
    name: err.name,
    ...(err.code && { code: err.code })
  });

  // Return sanitized message for client
  return sanitizeError(err, fallbackMessage);
}

