    margin-bottom: 12px;
}

/* Caption Actions */
.caption-actions {
    margin-bottom: 10px;
}

/* Caption Stats */
.caption-stats {
    display: flex;
    gap: 12px;
    margin-bottom: 10px;
    flex-wrap: wrap;
}

.caption-stats .stat-item {
    font-size: 10px;
    color: var(--text-muted);
}

/* Caption Status */
.caption-status {
    font-size: 10px;
    padding: 4px 0;
    min-height: 18px;
}

.caption-status-info {
    color: var(--accent-primary);
}

.caption-status-success {
    color: #28a745;
}

.caption-status-error {
    color: #dc3545;
}

/* Caption Preview */
.caption-preview-container {
    margin-bottom: 10px;
}

.caption-preview-list {
    max-height: 150px;
    overflow-y: auto;
}

.caption-preview-item {
    display: flex;
    gap: 8px;
    padding: 6px 8px;
    background: var(--bg-panel);
    border-radius: 4px;
    margin-bottom: 4px;
    font-size: 10px;
}

.caption-time {
    min-width: 40px;
    color: var(--accent-primary);
    font-weight: 500;
}

.caption-text {
    color: #e1e1e1;
    flex: 1;
}

.caption-keyword {
