    font-weight: 600;
}

.caption-preview-more {
    text-align: center;
    font-size: 10px;
    color: #666;
    padding: 6px;
}

/* Caption Export Actions */
.caption-export-actions {
    display: flex;
    gap: 6px;
    flex-wrap: wrap;
}

.caption-export-actions.hidden {
    display: none;
}

/* Text Editor Section */
.text-editor-section {
    background: var(--bg-card);
    border-radius: 6px;
    border: 1px solid var(--glass-border);
    margin-bottom: 10px;
}

.text-editor-panel {
    padding: 0 12px 12px;
    border-top: 1px solid var(--glass-border);
}

.text-editor-panel.collapsed {
    display: none;
}

/* Text Editor Bar */
.text-editor-bar {
    display: flex;
    align-items: center;
    gap: 8px;
    padding: 8px 0;
    font-size: 10px;
    color: var(--text-muted);
}

.text-editor-word-count {
    color: #666;
}

.text-editor-dirty {
    color: #ffeb3b;
    font-weight: bold;
}

.text-editor-status {
    flex: 1;
    text-align: right;
}

.text-editor-status-info {
    color: var(--accent-primary);
}

.text-editor-status-success {
    color: #28a745;
}

.text-editor-status-error {
    color: #dc3545;
}

/* Text Editor Toolbar */
.text-editor-toolbar {
    display: flex;
    gap: 6px;
    align-items: center;
    padding: 6px 0;
    margin-bottom: 8px;
    border-bottom: 1px solid #333;
}

.toolbar-divider {
    width: 1px;
    height: 18px;
    background: var(--glass-border);
    margin: 0 4px;
}

.btn-icon {
    width: 28px;
    height: 28px;
    padding: 0;
    font-size: 14px;
    display: flex;
    align-items: center;
    justify-content: center;
}

/* Text Editor Content */
.text-editor-content-wrapper {
    margin-bottom: 10px;
}

.text-editor-content {
    width: 100%;
    height: 180px;
    padding: 10px;
    background: var(--bg-panel);
    border: 1px solid var(--glass-border);
    border-radius: 4px;
    color: #e1e1e1;
    font-size: 11px;
    line-height: 1.5;
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif;
    resize: vertical;
}

.text-editor-content:focus {
    outline: none;
    border-color: var(--accent-primary);
}

.text-editor-content:disabled {
    opacity: 0.5;
    cursor: not-allowed;
}

.text-editor-content::placeholder {
    color: #666;
}

/* Search & Replace */
.text-editor-search {
    margin-bottom: 10px;
}

.search-row,
.replace-row {
    display: flex;
    gap: 6px;
    align-items: center;
    margin-bottom: 6px;
}

.search-input {
    flex: 1;
    min-width: 0;
    padding: 6px 8px;
    background: var(--bg-panel);
    border: 1px solid var(--glass-border);
    border-radius: 4px;
    color: #e1e1e1;
    font-size: 10px;
}

.search-input:focus {
    outline: none;
    border-color: var(--accent-primary);
}

.search-results {
    font-size: 10px;
    color: #666;
    min-width: 35px;
    text-align: center;
}

/* Text Editor Preview */
.text-editor-preview {
    margin-bottom: 10px;
    min-height: 0;
}

.preview-empty {
    padding: 12px;
    text-align: center;
    font-size: 10px;
    color: #666;
    background: var(--bg-panel);
    border-radius: 4px;
}

.preview-summary {
    display: flex;
    gap: 12px;
    margin-bottom: 8px;
    padding: 8px;
    background: var(--bg-panel);
    border-radius: 4px;
}

.preview-stat {
    font-size: 10px;
    color: var(--text-muted);
}

.preview-changes {
    max-height: 120px;
    overflow-y: auto;
}

.preview-change {
    display: flex;
    gap: 8px;
    align-items: center;
    padding: 6px 8px;
    background: var(--bg-panel);
    border-radius: 4px;
    margin-bottom: 4px;
    font-size: 10px;
    border-left: 3px solid #666;
}

.preview-change-deletion {
    border-left-color: #dc3545;
}

.preview-change-reorder {
    border-left-color: #ffeb3b;
}

.preview-change-insert {
    border-left-color: #28a745;
}

.change-type {
    min-width: 60px;
    text-transform: uppercase;
    font-size: 9px;
    font-weight: 600;
    color: var(--text-muted);
}

.change-desc {
    flex: 1;
    color: #e1e1e1;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}

.change-time {
    color: var(--accent-primary);
    font-weight: 500;
}

/* Text Editor Actions */
.text-editor-actions {
    display: flex;
    gap: 8px;
    flex-wrap: wrap;
}

.text-editor-actions .btn {
    flex: 1;
    min-width: 100px;
}

.text-editor-actions .btn.ready {
    animation: pulse 1.5s infinite;
}

@keyframes pulse {

    0%,
    100% {
        box-shadow: 0 0 0 0 rgba(74, 158, 255, 0.4);
    }

    50% {
        box-shadow: 0 0 0 8px rgba(74, 158, 255, 0);
    }
}

/* Social Reframe Section */
