    margin-bottom: 10px;
}

.reframe-actions .btn {
    flex: 1;
}

/* Reframe Status */
.reframe-status {
    font-size: 10px;
    padding: 4px 0;
    min-height: 18px;
}

.reframe-status-info {
    color: var(--accent-primary);
}

.reframe-status-success {
    color: #28a745;
}

.reframe-status-error {
    color: #dc3545;
}

/* Analysis Results */
