}

/**
 * Initialize all external link buttons that need CSInterface.openURLInDefaultBrowser()
 * CEP panels cannot use target="_blank" due to security restrictions
 */
function initExternalLinkButtons() {
    console.log('[SPLICE DEBUG] initExternalLinkButtons called');

    // Check if CSInterface is available
    if (typeof CSInterface === 'undefined') {
        console.error('[SPLICE DEBUG] CSInterface is not defined - cannot open external URLs');
        return;
    }

    let cs;
    try {
        cs = new CSInterface();
        console.log('[SPLICE DEBUG] CSInterface created successfully');
    } catch (e) {
        console.error('[SPLICE DEBUG] Failed to create CSInterface:', e);
        return;
    }

    // Buy a License button
    const buyLicenseBtn = document.querySelector('.btn-buy-license');
    console.log('[SPLICE DEBUG] buyLicenseBtn found:', !!buyLicenseBtn, buyLicenseBtn);

    if (buyLicenseBtn) {
        buyLicenseBtn.addEventListener('click', (e) => {
            console.log('[SPLICE DEBUG] Buy License button clicked!');
            e.preventDefault();
            e.stopPropagation();

            // VISUAL INDICATOR: Change button to show click was received
            buyLicenseBtn.textContent = 'Opening...';
            buyLicenseBtn.style.backgroundColor = '#28a745';

            try {
                console.log('[SPLICE DEBUG] Calling openURLInDefaultBrowser...');
                cs.openURLInDefaultBrowser('https://spliceclips.com/pricing');
                console.log('[SPLICE DEBUG] openURLInDefaultBrowser called successfully');

                // Reset button after a delay
                setTimeout(() => {
                    buyLicenseBtn.textContent = 'Buy License';
                    buyLicenseBtn.style.backgroundColor = '';
                }, 2000);
            } catch (err) {
                console.error('[SPLICE DEBUG] openURLInDefaultBrowser failed:', err);
                buyLicenseBtn.textContent = 'ERROR: ' + err.message;
                buyLicenseBtn.style.backgroundColor = '#dc3545';
            }
        });
        console.log('[SPLICE DEBUG] Click event listener attached to buyLicenseBtn');
    } else {
        console.error('[SPLICE DEBUG] .btn-buy-license button NOT FOUND in DOM');
    }

    // View Plans button (in upgrade modal)
    const viewPlansBtn = document.querySelector('.btn-view-plans');
    if (viewPlansBtn) {
        viewPlansBtn.addEventListener('click', (e) => {
            e.preventDefault();
            e.stopPropagation();
            const url = viewPlansBtn.dataset.url || 'https://spliceclips.com/pricing';
            cs.openURLInDefaultBrowser(url);
        });
    }

    // Referral link button
    const referralBtn = document.querySelector('.btn-referral');
    if (referralBtn) {
        referralBtn.addEventListener('click', (e) => {
            e.preventDefault();
            e.stopPropagation();
            const url = referralBtn.dataset.url || 'https://splice.video/referrals';
            cs.openURLInDefaultBrowser(url);
        });
    }

