// Export Audio functionality
// WAV 48kHz 16-bit preset path
const WAV_PRESET_PATH = '/Applications/Adobe Premiere Pro (Beta)/Adobe Premiere Pro (Beta).app/Contents/Settings/EncoderPresets/Wave48mono16.epr';

document.getElementById('exportAudioBtn').addEventListener('click', async () => {
  const statusEl = document.getElementById('status');
  const btn = document.getElementById('exportAudioBtn');

  btn.disabled = true;
  statusEl.textContent = 'Preparing audio export...';

  try {
    const project = await ppro.Project.getActiveProject();
    if (!project) {
      statusEl.textContent = 'No project open';
      btn.disabled = false;
      return;
    }

    const sequence = await project.getActiveSequence();
    if (!sequence) {
      statusEl.textContent = 'No active sequence';
      btn.disabled = false;
      return;
    }

    // Get EncoderManager
    const encoderManager = ppro.EncoderManager.getManager();
    if (!encoderManager) {
      statusEl.textContent = 'EncoderManager not available';
      btn.disabled = false;
      return;
    }

    statusEl.textContent = 'Select save location...';

    // Use UXP file system to get save location
    const fs = require('uxp').storage.localFileSystem;
    const file = await fs.getFileForSaving('splice_audio.wav', {
      types: ['wav']
    });

    if (!file) {
      statusEl.textContent = 'Export cancelled';
      btn.disabled = false;
      return;
    }

    const outputPath = file.nativePath;
    statusEl.textContent = `Exporting to: ${outputPath}`;

    // Export with WAV preset
    const result = await encoderManager.exportSequence(
      sequence,
      ppro.EncoderManager.EXPORT_IMMEDIATELY,
      outputPath,
      WAV_PRESET_PATH,
      true // exportFull - export entire sequence
    );

    if (result) {
      statusEl.textContent = `Export complete: ${outputPath}`;
    } else {
      statusEl.textContent = 'Export returned false - check console';
    }

  } catch (err) {
    statusEl.textContent = `Export error: ${err.message}`;
    console.error('SPLICE export error:', err);
    console.error('Full error:', JSON.stringify(err, null, 2));
  }

  btn.disabled = false;
