      statusEl.textContent = 'EncoderManager not available';
      btn.disabled = false;
      return;
    }

    statusEl.textContent = 'EncoderManager ready. Getting file picker...';

    // Use UXP file system to get save location
    const fs = require('uxp').storage.localFileSystem;
    const file = await fs.getFileForSaving('splice_audio.wav', {
      types: ['wav', 'mp3']
    });

    if (!file) {
      statusEl.textContent = 'Export cancelled';
      btn.disabled = false;
      return;
