  2020                // Generate a clean font name from filename
  2021                const fontName = file.name.replace(/\.[^.]+$/, '').replace(/[^a-zA-Z0-9\s-]/g, '');
  2022                const fontId = 'custom-' + Date.now();
  2023
  2024                // Create FontFace and load it
